import React, { useState, useEffect, useRef } from "react";

const AudioList = () => {
  const [audioFiles, setAudioFiles] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => setAudioFiles(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handlePlayPause = () => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
      setIsPlaying(!isPlaying);
    }
  };

  const handlePlayAudio = async (audioId, trackName, author, imageUrl) => {
    try {
      const response = await fetch(`http://localhost:8080/audio/${audioId}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const blob = await response.blob();
      const audioData = URL.createObjectURL(blob);
      setCurrentTrack({ audioUrl: audioData, trackName, author, imageUrl });
      setIsPlaying(true); // Начинаем проигрывать аудио сразу после загрузки
    } catch (error) {
      console.error("Error fetching audio:", error);
    }
  };

  return (
    <div>
      <h1>Play Audio Files</h1>
      <ul style={{ listStyleType: "none", padding: 0 }}>
        {audioFiles.map((audioFile) => (
          <li
            key={audioFile.id}
            style={{
              display: "flex",
              alignItems: "center",
              marginBottom: "10px",
            }}
          >
            <div style={{ marginRight: "10px", display: "flex", alignItems: "center" }}>
              {audioFile.image && (
                <img
                  src={`data:image/jpeg;base64,${audioFile.image.data}`}
                  alt={audioFile.title}
                  style={{ maxWidth: "50px", maxHeight: "50px", marginRight: "10px" }}
                />
              )}
              <button
                onClick={() =>
                  handlePlayAudio(
                    audioFile.id,
                    audioFile.title,
                    audioFile.author,
                    `data:image/jpeg;base64,${audioFile.image.data}`
                  )
                }
                style={{
                  marginRight: "10px",
                  padding: "5px 10px",
                  backgroundColor: "#007BFF",
                  color: "white",
                  border: "none",
                  borderRadius: "4px",
                  cursor: "pointer",
                  height: "30px",
                }}
              >
                {isPlaying && currentTrack?.audioUrl === audioFile.id ? "Pause" : "Play"}
              </button>
              <div style={{ display: "flex", flexDirection: "column" }}>
                <span style={{ fontSize: "14px", fontWeight: "bold", marginBottom: "5px" }}>
                  {audioFile.title}
                </span>
                <span style={{ fontSize: "14px", color: "#6c757d" }}>
                  {audioFile.author}
                </span>
              </div>
            </div>
          </li>
        ))}
      </ul>

      {/* Кастомный Controls */}
      {currentTrack && (
        <div
          id="customControls"
          style={{
            backgroundColor: "#f0f0f0",
            padding: "10px",
            borderRadius: "4px",
            position: "fixed",
            bottom: 0,
            left: 0,
            right: 0,
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            boxSizing: "border-box",
            width: "100%",
            margin: "0 auto",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <img
              id="audioImage2"
              src={currentTrack.imageUrl}
              style={{
                maxWidth: "50px",
                maxHeight: "50px",
                marginRight: "10px",
              }}
            />
            <div style={{ display: "flex", flexDirection: "column" }}>
              <span
                id="customTrackName"
                style={{
                  fontSize: "16px",
                  fontWeight: "bold",
                  marginBottom: "5px",
                }}
              >
                {currentTrack.trackName}
              </span>
              <span
                id="customAuthor"
                style={{ fontSize: "14px", color: "#6c757d" }}
              >
                {currentTrack.author}
              </span>
            </div>
          </div>
          <audio
            id="customAudioPlayer"
            ref={audioRef}
            src={currentTrack.audioUrl}
            controls
            style={{ width: "100%", marginLeft: "10px" }}
            type="audio/mpeg"
            onEnded={() => setIsPlaying(false)} // Когда аудио заканчивается, меняем состояние на "остановлено"
          >
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
    </div>
  );
};

export default AudioList;
