import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useAudioContext } from "../AudioContext";
import "./PlaylistContainer.css";

const PlaylistContainer = () => {
  const [playlists, setPlaylists] = useState([]);
  const { currentPlaylistId } = useAudioContext();
  const [currentPLaylist, setCurrentPlaylist] = useState(null);

  useEffect(() => {
    // Здесь вы можете выполнить запрос к серверу для получения списка плейлистов
    // Пример:
    fetch("http://localhost:8080/api/playlists")
      .then((response) => response.json())
      .then((data) => setPlaylists(data))
      .catch((error) => console.error("Error fetching playlists:", error));
  }, []);

  return (
    <div className="playlist-container">
      <h2>Playlists</h2>
      <div className="playlist-list">
        {playlists.map((playlist) => (
          <div
            key={playlist.id}
            className="playlist-item"
          >
            <Link to={`/playlists/${playlist.id}`}>
              <h3>{playlist.name}</h3>
              <p>Author: {playlist.author}</p>
              <p>Tracks: {playlist.countOfAudio}</p>
              <p>Duration: {playlist.duration}</p>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PlaylistContainer;
