import React, { useEffect, useRef } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioControls.css";

const AudioControls = () => {
  const {
    audioFiles,
    currentIndex,
    setCurrentIndex,
    isPlaying,
    volume,
    setVolume,
    setCurrentTrack,
    setNextTrack,
  } = useAudioContext();
  const audioRef = useRef(null);

  useEffect(() => {
    if (audioRef.current && isPlaying) {
      audioRef.current.play();
    }
  }, [isPlaying]);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = volume;
    }
  }, [volume]);

  useEffect(() => {
    if (audioRef.current && currentIndex >= 0 && currentIndex < audioFiles.length) {
      const track = audioFiles[currentIndex];
      setCurrentTrack(track);
      audioRef.current.src = track.audioUrl;
      audioRef.current.play();
      const nextIndex = (currentIndex + 1) % audioFiles.length;
      const nextTrack = audioFiles[nextIndex];
      setNextTrack(nextTrack);
      setCurrentIndex(nextIndex);
    }
  }, [currentIndex, audioFiles, setCurrentTrack, setNextTrack, setCurrentIndex]);

  return (
    <div className="audio-controls">
      <div className="custom-controls">
        <div className="meta-data">
          <img
            id="audioImage2"
            src={currentTrack ? currentTrack.imageUrl : ""}
            alt={currentTrack ? currentTrack.trackName : ""}
          />
          <div className="title-author">
            <span className="title">{currentTrack ? currentTrack.trackName : "Track Name"}</span>
            <span className="author">{currentTrack ? currentTrack.author : "Author"}</span>
          </div>
        </div>
        <audio
          id="customAudioPlayer"
          ref={audioRef}
          controls
          autoPlay
          type="audio/mpeg"
        >
          Your browser does not support the audio element.
        </audio>
        <div className="volume-container">
          <input
            className="volume"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={(e) => setVolume(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
};

export default AudioControls;
