const updateAudioFiles = (data) => {
  setAudioFiles(data);
  if (currentTrackIndex !== -1) {
    const currentAudioFile = data[currentTrackIndex];
    const { id, title, author, image } = currentAudioFile;
    const imageUrl = image ? `data:image/jpeg;base64,${image.data}` : "";
    const audioUrl = currentAudioFile.audioUrl; // Заменить на актуальный способ получения URL аудио

    setCurrentTrack({
      id,
      audioUrl,
      trackName: title,
      author,
      imageUrl,
    });
  }
};
