import React, { useEffect, useRef, useState  } from "react";
import { useAudioContext } from "../AudioContext";

const AudioControls = () => {
  const { currentTrack, isPlaying, volume, audioRef, setVolume, audioFiles, currentTrackIndex,setCurrentTrackIndex, setCurrentTrack } = useAudioContext();
  
  // useEffect(() => {
  //   if (audioRef.current) {
  //     audioRef.current.volume = volume;
  //     audioRef.current.addEventListener("ended", () => {
  //       const nextIndex = (currentTrackIndex + 1) % audioFiles.length;
  //       setCurrentTrackIndex(nextIndex);
  //       setCurrentTrack(audioFiles[nextIndex]);
  //     });
  //   }
  // }, [volume, audioFiles, currentTrackIndex]);

  return (
    <div className="audio-controls">
      <div className="custom-controls">
        <div className="meta-data">
          {currentTrack ? (
            <>
              <img id="audioImage2" src={currentTrack.imageUrl} />
              <div className="title-author">
                <span className="title">{currentTrack.trackName}</span>
                <span className="author">{currentTrack.author}</span>
              </div>
            </>
          ) : (
            <>
              <img id="audioImage2" src="" />
              <div className="title-author">
                <span className="title">Track Name</span>
                <span className="author">Author</span>
              </div>
            </>
          )}
        </div>
        <audio
          id="customAudioPlayer"
          ref={audioRef}
          src={currentTrack ? currentTrack.audioUrl : ""}
          controls
          autoPlay={isPlaying}
          type="audio/mpeg"
        >
          Your browser does not support the audio element.
        </audio>
        <div className="volume-container">
          <input
            className="volume"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={(e) => setVolume(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
};

export default AudioControls;
