import React, { useState, useEffect, useRef } from "react";
import "./AudioControls.css";

const AudioControls = ({
  currentTrack,
  isPlaying,
  volume,
  audioRef,
  setVolume,
}) => {
  return (
    <div className="audio-controls">
      {/* Кастомный Controls */}
      {currentTrack && (
        <div id="customControls">
          <div style={{ display: "flex", alignItems: "center" }}>
            <img
              id="audioImage2"
              src={currentTrack.imageUrl}
              alt={currentTrack.title}
            />
            <div>
              <span id="customTrackName">{currentTrack.trackName}</span>
              <span id="customAuthor">{currentTrack.author}</span>
            </div>
          </div>
          <audio
            id="customAudioPlayer"
            ref={audioRef} // Используйте audioRef для управления аудиоэлементом
            src={currentTrack.audioUrl}
            controls
            autoPlay={isPlaying}
            style={{ width: "1200px" }}
            type="audio/mpeg"
          >
            Your browser does not support the audio element.
          </audio>
          <div id="customVolume">
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={(e) => setVolume(Number(e.target.value))}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default AudioControls;
