import React, { useState, useEffect, useRef } from "react";

const AudioList = () => {
  const [audioFiles, setAudioFiles] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const audioRef = useRef(null);
  const [volume, setVolume] = useState(1);

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => setAudioFiles(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = volume;
    }
  }, [volume]);

  const handlePlayAudio = async (index) => {
    if (index === currentTrackIndex) {
      setIsPlaying(!isPlaying);
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
    } else {
      try {
        const audioFile = audioFiles[index];
        const response = await fetch(`http://localhost:8080/audio/${audioFile.id}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const blob = await response.blob();
        const audioData = URL.createObjectURL(blob);
        setCurrentTrackIndex(index);
        setIsPlaying(true);
      } catch (error) {
        console.error("Error fetching audio:", error);
      }
    }
  };

  const handleEnded = () => {
    if (audioFiles.length > 0) {
      const nextIndex = (currentTrackIndex + 1) % audioFiles.length;
      handlePlayAudio(nextIndex);
    }
  };

  return (
    <div
      style={{
        borderRadius: "10px",
        backgroundColor: "#ffffff",
        padding: "20px",
        width: "400px",
        display: "flex",
        margin: "0 auto",
      }}
    >
      <ul style={{ listStyleType: "none", padding: 0 }}>
        {audioFiles.map((audioFile) => (
          <li
            key={audioFile.id}
            style={{
              display: "flex",
              alignItems: "center",
              marginBottom: "10px",
            }}
          >
            <div
              style={{
                marginRight: "10px",
                display: "flex",
                alignItems: "center",
                border: "1px solid grey",
                borderRadius: "4px",
                padding: "7px",
              }}
            >
              {audioFile.image && (
                <img
                  src={`data:image/jpeg;base64,${audioFile.image.data}`}
                  alt={audioFile.title}
                  style={{
                    width: "50px",
                    height: "50px",
                    marginRight: "10px",
                  }}
                />
              )}
              <div
                style={{
                  width: "50px",
                }}
              >
                <button
                  onClick={() =>
                    handlePlayAudio(
                      audioFile.id,
                      audioFile.title,
                      audioFile.author,
                      `data:image/jpeg;base64,${audioFile.image.data}`
                    )
                  }
                  style={{
                    marginRight: "10px",
                    padding: "5px 10px",
                    backgroundColor: "#b9b5ff",
                    color: "white",
                    fontSize: "20px",
                    fontWeight: "bold",
                    border: "none",
                    borderRadius: "4px",
                    cursor: "pointer",
                    height: "40px",
                    width: "40px",
                    borderRadius: "100px",
                  }}
                >
                  {currentTrack && currentTrack.id === audioFile.id && isPlaying
                    ? "||"
                    : ">"}
                </button>
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  width: "220px",
                }}
              >
                <span
                  style={{
                    fontSize: "18px",
                    fontWeight: "bold",
                    marginBottom: "5px",
                  }}
                >
                  {audioFile.title}
                </span>
                <span style={{ fontSize: "16px", color: "#6c757d" }}>
                  {audioFile.author}
                </span>
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  marginRight: "20px",
                }}
              >
                <span
                  style={{
                    fontSize: "18px",
                    color: "grey",
                    fontWeight: "lighter",
                    marginBottom: "5px",
                  }}
                >
                  {audioFile.duration}
                </span>
              </div>
            </div>
          </li>
        ))}
      </ul>

      {/* Кастомный Controls */}
      {currentTrackIndex !== null && (
        <div id="customControls">
          {/* ... rest of your custom controls ... */}

          <audio
            id="customAudioPlayer"
            ref={audioRef}
            src={audioFiles[currentTrackIndex].audioUrl}
            controls
            autoPlay={isPlaying}
            onEnded={handleEnded} // Add the onEnded event listener
            style={{ width: "1200px", marginRight: "130px" }}
            type="audio/mpeg"
          >
            Your browser does not support the audio element.
          </audio>

          {/* ... rest of your custom controls ... */}
        </div>
      )}
    </div>
  );
};

export default AudioList;
