import React, { useState, useEffect } from "react";

const AudioList = () => {
  const [audioFiles, setAudioFiles] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => setAudioFiles(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handlePlayAudio = (audioUrl, trackName, imageUrl) => {
    customAudioPlayer.src = audioUrl;
    customTrackName.innerText = trackName;
    audioImage2.src = imageUrl;

    customAudioPlayer.play();
  };

  // const customControlsStyle = {
  //   display: "none",
  //   position: "fixed",
  //   bottom: 0,
  //   left: 0,
  //   width: "100%",
  //   background: "#333",
  //   color: "#fff",
  //   padding: "10px"
  // };

  return (
    <div>
      <h1>Play Audio Files</h1>
      <ul>
        {audioFiles.map((audioFile) => (
          <li key={audioFile.id}>
            <div>
              <div>
                {audioFile.image && (
                  <img
                    src={`data:image/jpeg;base64,${audioFile.image.data}`}
                    alt={audioFile.title}
                    style={{ maxWidth: "50px", maxHeight: "50px" }}
                  />
                )}
              </div>
              <div className="control-wrap">
                <div
                  className="control play"
                  onClick={() => handlePlayAudio(audioFile.audioUrl, `${audioFile.author} - ${audioFile.title}`, `data:image/jpeg;base64,${audioFile.image.data}`)}
                >
                  <span>{audioFile.author} - {audioFile.title}</span>
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>

      {/* Кастомный Controls */}
      <div id="customControls">
        <span id="customTrackName"></span>
        <div style={{ flexShrink: 0 }}>
          <img id="audioImage2" style={{ maxWidth: "50px", maxHeight: "50px" }} />
        </div>
        <audio id="customAudioPlayer" controls style={{ width: "100%" }} type="audio/mpeg">
          Your browser does not support the audio element.
        </audio>
      </div>
    </div>
  );
};

export default AudioList;
