// В AudioList.js

import React, { useEffect } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioList.css";

const AudioList = () => {
  const {
    setCurrentTrack,
    isPlaying,
    setIsPlaying,
    togglePlay,
    currentTrackIndex,
    setCurrentTrackIndex,
    updateAudioFiles,
    audioFiles,
    audioRef,
  } = useAudioContext();

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => updateAudioFiles(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  // Обновленный handleTogglePlay для сохранения текущей позиции аудиозаписи перед вызовом pause()
  const handleTogglePlay = () => {
    if (isPlaying) {
      audioRef.current.pause(); // Пауза воспроизведения
    } else {
      const currentTime = audioRef.current.currentTime; // Сохраняем текущее время воспроизведения
      audioRef.current.play(); // Возобновление воспроизведения
      audioRef.current.currentTime = currentTime; // Устанавливаем сохраненное время воспроизведения
    }
    togglePlay(); // Переключение состояния воспроизведения
  };

  const handlePlayAudio = async (audioFile, index) => {
    try {
      const response = await fetch(
        `http://localhost:8080/audio/${audioFile.id}`
      );
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const blob = await response.blob();
      const audioData = URL.createObjectURL(new Blob([blob]));

      setCurrentTrackIndex(index);
      setCurrentTrack({
        id: audioFile.id,
        audioUrl: audioData,
        trackName: audioFile.title,
        author: audioFile.author,
        imageUrl: audioFile.image
          ? `data:image/jpeg;base64,${audioFile.image.data}`
          : "",
      });
      setIsPlaying(true);
    } catch (error) {
      console.error("Error fetching audio:", error);
    }
  };

  return (
    <div className="audio-list">
      <ul>
        {audioFiles.map((audioFile, index) => (
          <li key={audioFile.id}>
            <div className="audio-metadata-container">
              {audioFile.image && (
                <img
                  src={`data:image/jpeg;base64,${audioFile.image.data}`}
                  alt={audioFile.title}
                />
              )}
              <div className="button-container">
                <button
                  className="play_pause"
                  onClick={() => {
                    handlePlayAudio(audioFile, index);
                    handleTogglePlay(); // Вызываем обработчик только для переключения состояния воспроизведения
                  }}
                >
                  {currentTrackIndex === index
                    ? isPlaying
                      ? "Pause"
                      : "Play"
                    : "Play"}
                  Play
                </button>
              </div>
              <div className="title-author-container">
                <span className="title">{audioFile.title}</span>
                <span>{audioFile.author}</span>
              </div>
              <div className="duration-container">
                <span className="duration">{audioFile.duration}</span>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AudioList;
