import React, {
  createContext,
  useState,
  useContext,
  useEffect,
  useRef,
} from "react";
import PropTypes from "prop-types";
import { debounce } from "lodash";

const AudioContext = createContext();

export const useAudioContext = () => useContext(AudioContext);

export const AudioProvider = ({ children }) => {
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);
  const [audioFiles, setAudioFiles] = useState([]);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(-1);
  const audioRef = useRef(null);

  const updateAudioFiles = (data) => {
    setAudioFiles(data);
  };

  const togglePlay = () => {
    setIsPlaying(!isPlaying);
  };

  useEffect(() => {
    if (currentTrackIndex !== -1) {
      setCurrentTrack({
        id: audioFiles[currentTrackIndex].id,
        audioUrl: audioFiles[currentTrackIndex].audioUrl,
        trackName: audioFiles[currentTrackIndex].title,
        author: audioFiles[currentTrackIndex].author,
        imageUrl: audioFiles[currentTrackIndex].image
          ? `data:image/jpeg;base64,${audioFiles[currentTrackIndex].image.data}`
          : "",
      });
    }
  }, [audioFiles, currentTrackIndex]);

  const playNextTrack = () => {
    if (currentTrackIndex === audioFiles.length - 1) return;
    const nextIndex = currentTrackIndex + 1;
    setCurrentTrackIndex(nextIndex);
    setCurrentTrack({
      id: audioFiles[nextIndex].id,
      audioUrl: audioFiles[nextIndex].audioUrl,
      trackName: audioFiles[nextIndex].title,
      author: audioFiles[nextIndex].author,
      imageUrl: audioFiles[nextIndex].image
        ? `data:image/jpeg;base64,${audioFiles[nextIndex].image.data}`
        : "",
    });
  };

  const playPreviousTrack = () => {
    if (currentTrackIndex === 0) return;
    const previousIndex = currentTrackIndex - 1;
    setCurrentTrackIndex(previousIndex);
    setCurrentTrack({
      id: audioFiles[previousIndex].id,
      audioUrl: audioFiles[previousIndex].audioUrl,
      trackName: audioFiles[previousIndex].title,
      author: audioFiles[previousIndex].author,
      imageUrl: audioFiles[previousIndex].image
        ? `data:image/jpeg;base64,${audioFiles[previousIndex].image.data}`
        : "",
    });
  };

  // Задержка перед следующим вызовом
  const debouncedPlayNextTrack = debounce(playNextTrack, 500);
  const debouncedPlayPreviousTrack = debounce(playPreviousTrack, 500);

  useEffect(() => {
    if (currentTrackIndex !== -1 && isPlaying) {
      const fetchAudioAndPlay = async () => {
        try {
          const response = await fetch(
            `http://localhost:8080/audio/${audioFiles[currentTrackIndex].id}`
          );
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          const blob = await response.blob();
          const audioData = URL.createObjectURL(new Blob([blob]));

          setCurrentTrack({
            id: audioFiles[currentTrackIndex].id,
            audioUrl: audioData,
            trackName: audioFiles[currentTrackIndex].title,
            author: audioFiles[currentTrackIndex].author,
            imageUrl: audioFiles[currentTrackIndex].image
              ? `data:image/jpeg;base64,${audioFiles[currentTrackIndex].image.data}`
              : "",
          });
          setIsPlaying(true);
          console.log("Вызов из AudioContext:" + isPlaying)
        } catch (error) {
          console.error("Error fetching audio:", error);
        }
      };

      fetchAudioAndPlay();
    }
  }, [currentTrackIndex]);

  return (
    <AudioContext.Provider
      value={{
        currentTrack,
        setCurrentTrack,
        isPlaying,
        setIsPlaying,
        togglePlay,
        volume,
        setVolume,
        audioFiles,
        updateAudioFiles,
        currentTrackIndex,
        setCurrentTrackIndex,
        audioRef,
        debouncedPlayNextTrack,
        debouncedPlayPreviousTrack,
      }}
    >
      {children}
    </AudioContext.Provider>
  );
};

AudioProvider.propTypes = {
  children: PropTypes.node.isRequired,
};
