import React, { useState, useEffect, useRef } from "react";

const AudioControls = ({
  currentTrack,
  isPlaying,
  volume,
  audioRef,
  setVolume,
}) => {
  return (
    <div
      style={{
        borderRadius: "10px",
        backgroundColor: "#ffffff",
        padding: "20px",
        width: "400px",
        display: "flex",
        margin: "0 auto",
      }}
    >
      {/* Кастомный Controls */}
      {currentTrack && (
        <div
          id="customControls"
          style={{
            backgroundColor: "#f0f0f0",
            padding: "10px",
            borderRadius: "4px",
            position: "fixed",
            bottom: 0,
            left: 0,
            right: 0,
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            boxSizing: "border-box",
            width: "100%",
            margin: "0 auto",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <img
              id="audioImage2"
              src={currentTrack.imageUrl}
              style={{
                width: "70px",
                height: "70px",
                marginRight: "10px",
              }}
            />
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                marginLeft: "10px",
                width: "200px",
              }}
            >
              <span
                id="customTrackName"
                style={{
                  fontSize: "20px",
                  fontWeight: "bold",
                  marginBottom: "5px",
                }}
              >
                {currentTrack.trackName}
              </span>
              <span
                id="customAuthor"
                style={{ fontSize: "18px", color: "#6c757d" }}
              >
                {currentTrack.author}
              </span>
            </div>
          </div>
          <audio
            id="customAudioPlayer"
            ref={audioRef}
            src={currentTrack.audioUrl}
            controls
            autoPlay={isPlaying}
            style={{ width: "1200px", marginRight: "130px" }}
            type="audio/mpeg"
          >
            Your browser does not support the audio element.
          </audio>
          <div style={{ display: "flex", alignItems: "center" }}>
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={(e) => setVolume(e.target.value)}
              style={{ width: "100px", marginRight: "100px" }}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default AudioControls;
