import React, { useEffect } from "react";
import { useParams } from "react-router-dom"; // Импорт хука useParams
import { useAudioContext } from "../AudioContext";
import "./AudioList.css";

const AudioList = () => {
  const { id } = useParams(); // Получение параметра id из URL

  const {
    setCurrentTrack,
    isPlaying,
    togglePlay,
    setIsPlaying,
    currentTrackIndex,
    setCurrentTrackIndex,
    updateAudioFiles,
    audioFiles,
    audioRef,
    clearAudioFiles,
    currentPlaylistId,
    setCurrentPlaylistId,
    playlistId, setPlaylistId
  } = useAudioContext();

  // useEffect(() => {
  //   clearAudioFiles(); // Вызывается только при монтировании
  // }, []);

  useEffect(() => {
    setPlaylistId(id);
    if (
      id &&
      typeof id === "string" &&
      currentPlaylistId !== id
    ) {
      fetch(`http://localhost:8080/api/playlists/${id}`, { method: "GET" })
        .then((response) => response.json())
        .then((data) => updateAudioFiles(data))
        .catch((error) => console.error("Error fetching data:", error));
    }
  }, [id]);

  const handleTogglePlay = () => {
    if (isPlaying) {
      audioRef.current.pause(); // Пауза воспроизведения
    } else {
      audioRef.current.play(); // Возобновление воспроизведения
    }
    togglePlay(); // Переключение состояния воспроизведения
  };

  const handlePlayAudio = async (audioFile, index) => {
    if (currentPlaylistId !== id) {
      // Если плейлист изменился, обновляем контекст
      setCurrentPlaylistId(playlistId);
    }
    if (currentTrackIndex === index && currentPlaylistId === playlistId) {
      handleTogglePlay();
    } else {
      try {
        const response = await fetch(
          `http://localhost:8080/api/audio/${audioFile.id}`
        );
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const blob = await response.blob();
        const audioData = URL.createObjectURL(new Blob([blob]));

        setCurrentTrackIndex(index);
        setCurrentTrack({
          id: audioFile.id,
          audioUrl: audioData,
          trackName: audioFile.title,
          author: audioFile.author,
          imageUrl: audioFile.image
            ? `data:image/jpeg;base64,${audioFile.image.data}`
            : "",
          duration: audioFile.duration,
        });
        setIsPlaying(true); // Устанавливаем флаг воспроизведения, чтобы воспроизвести трек
      } catch (error) {
        console.error("Error fetching audio:", error);
      }
    }
  };

  function formatDuration(duration) {
    const hours = Math.floor(duration / 3600);
    const minutes = Math.floor((duration % 3600) / 60);
    const seconds = Math.floor(duration % 60);

    let formattedTime = "";
    if (hours > 0) {
      formattedTime += hours + ":";
    }
    if (hours > 0 && minutes < 10) {
      formattedTime += "0";
    }
    formattedTime += String(minutes).padStart(hours > 0 ? 2 : 1, "0") + ":";
    formattedTime += String(seconds).padStart(2, "0");

    return formattedTime;
  }

  return (
    <div className="audio-list">
      <ul>
        {Array.isArray(audioFiles) &&
          audioFiles.map((audioFile, index) => (
            <li key={audioFile.id}>
              <div className="audio-metadata-container">
                {audioFile.image && (
                  <img
                    src={`data:image/jpeg;base64,${audioFile.image.data}`}
                    alt={audioFile.title}
                    loading="lazy"
                  />
                )}
                <div className="button-container">
                  <button
                    className="play_pause"
                    onClick={() => handlePlayAudio(audioFile, index)}
                  >
                    {currentTrackIndex === index && isPlaying ? "||" : ">"}
                  </button>
                </div>
                <div className="title-author-container">
                  <span className="title">{audioFile.title}</span>
                  <span>{audioFile.author}</span>
                </div>
                <div className="duration-container">
                  <span className="duration">
                    {formatDuration(audioFile.duration)}
                  </span>
                </div>
              </div>
            </li>
          ))}
      </ul>
    </div>
  );
};

export default AudioList;
