import React, { useState, useEffect } from "react";

const AudioList = () => {
  const [audioFiles, setAudioFiles] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => setAudioFiles(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handlePlayAudio = async (audioId, trackName, imageUrl) => {
    try {
      const response = await fetch(`http://localhost:8080/audio/${audioId}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const blob = await response.blob();
      const audioData = URL.createObjectURL(blob);
      setCurrentTrack({ audioUrl: audioData, trackName, imageUrl });
    } catch (error) {
      console.error("Error fetching audio:", error);
    }
  };

  return (
    <div>
      <h1>Play Audio Files</h1>
      <ul style={{ listStyleType: "none", padding: 0 }}>
        {audioFiles.map((audioFile) => (
          <li key={audioFile.id} style={{ display: "flex", alignItems: "center", marginBottom: "10px" }}>
            <div style={{ marginRight: "10px" }}>
              {audioFile.image && (
                <img
                  src={`data:image/jpeg;base64,${audioFile.image.data}`}
                  alt={audioFile.title}
                  style={{ maxWidth: "50px", maxHeight: "50px" }}
                />
              )}
            </div>
            <div style={{ display: "flex", flexDirection: "column" }}>
              <div className="control-wrap" style={{ display: "flex", alignItems: "center" }}>
                <button
                  onClick={() =>
                    handlePlayAudio(
                      audioFile.id,
                      `${audioFile.author} - ${audioFile.title}`,
                      `data:image/jpeg;base64,${audioFile.image.data}`
                    )
                  }
                  style={{ marginRight: "10px", padding: "5px 10px", backgroundColor: "#007BFF", color: "white", border: "none", borderRadius: "4px", cursor: "pointer" }}
                >
                  Play
                </button>
                <span style={{ fontSize: "14px" }}>
                  {audioFile.author} - {audioFile.title}
                </span>
              </div>
            </div>
          </li>
        ))}
      </ul>

      {/* Кастомный Controls */}
      {currentTrack && (
        <div id="customControls" style={{ display: "flex", flexDirection: "column", backgroundColor: "#f0f0f0", padding: "10px", borderRadius: "4px", marginTop: "20px" }}>
          <span id="customTrackName" style={{ fontSize: "16px", fontWeight: "bold", marginRight: "10px" }}>{currentTrack.trackName}</span>
          <div style={{ flexShrink: 0, marginRight: "10px" }}>
            <img
              id="audioImage2"
              src={currentTrack.imageUrl}
              style={{ maxWidth: "50px", maxHeight: "50px" }}
            />
          </div>
          <audio
            id="customAudioPlayer"
            src={currentTrack.audioUrl}
            controls
            style={{ width: "100%" }}
            type="audio/mpeg"
          >
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
    </div>
  );
};

export default AudioList;
