// App.jsx
import React, { useRef } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import AudioList from "./components/audioList/AudioList";
import UploadForm from "./components/uploadAudio/UploadForm";
import Navbar from "./components/Navbar";
import { AudioProvider, useAudioContext } from "./components/AudioContext";
import AudioControls from "./components/audioControls/AudioControls";

function App() {
  const audioRef = useRef(null);
  const { currentTrack, setCurrentTrack, setIsPlaying } = useAudioContext();

  // Функция для обработки воспроизведения аудио
  const handlePlayAudio = (audioData) => {
    if (audioRef.current) {
      audioRef.current.src = audioData.audioUrl;
      setCurrentTrack(audioData);
      setIsPlaying(true);
    }
  };

  return (
    <div className="App">
      <Router>
        <Navbar />
        <AudioProvider>
          <AudioControls audioRef={audioRef} /> {/* AudioControls всегда отображается */}
          <Routes>
            <Route
              path="/audio/upload"
              element={<UploadForm />}
            />
            <Route
              path="/audio"
              element={<AudioList handlePlayAudio={handlePlayAudio} />}
            />
          </Routes>
        </AudioProvider>
      </Router>
    </div>
  );
}

export default App;
