import React, { useState, useEffect, useRef } from "react";
import "./AudioList.css";
import AudioControls from "../audioControls/AudioControls";
import { useAudioContext } from "../AudioContext";

const AudioList = () => {
  const { audioFiles, setCurrentTrack, setIsPlaying } = useAudioContext();
  const [audioData, setAudioData] = useState([]);
  const audioRef = useRef(null);
  const [volume, setVolume] = useState(1);

  useEffect(() => {
    fetch("http://localhost:8080/audio")
      .then((response) => response.json())
      .then((data) => setAudioData(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handlePlayAudio = (audio) => {
    setCurrentTrack(audio);
    setIsPlaying(true);
  };

  return (
    <div className="audio-list">
      <ul>
        {audioData.map((audio) => (
          <li key={audio.id}>
            <div className="audio-metadata-container">
              {audio.image && (
                <img
                  src={`data:image/jpeg;base64,${audio.image.data}`}
                  alt={audio.title}
                />
              )}
              <div className="button-container">
                <button
                  className="play_pause"
                  onClick={() => handlePlayAudio(audio)}
                >
                  ▶️
                </button>
              </div>
              <div className="title-author-container">
                <span className="title">{audio.title}</span>
                <span>{audio.author}</span>
              </div>
              <div className="duration-conteiner">
                <span className="duration">{audio.duration}</span>
              </div>
            </div>
          </li>
        ))}
      </ul>
      {/* Кастомные контролы аудио */}
      <AudioControls audioRef={audioRef} volume={volume} setVolume={setVolume} />
    </div>
  );
};

export default AudioList;
