// AudioProvider.js
import React, { createContext, useState, useContext, useEffect, useRef } from "react";
import PropTypes from "prop-types";

const AudioContext = createContext();

export const useAudioContext = () => useContext(AudioContext);

export const AudioProvider = ({ children }) => {
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);
  const [audioFiles, setAudioFiles] = useState([]);
  const [currentTrackIndex, setCurrentTrackIndex] = useState(-1);
  const audioRef = useRef(null);

  const updateAudioFiles = (data) => {
    setAudioFiles(data);
    if (currentTrackIndex !== -1) {
      setCurrentTrack({
        id: data[currentTrackIndex].id,
        audioUrl: data[currentTrackIndex].audioUrl, // Оставляем пустым, так как нам нужно заново загрузить аудио при переключении трека
        trackName: data[currentTrackIndex].title,
        author: data[currentTrackIndex].author,
        imageUrl: data[currentTrackIndex].image ? `data:image/jpeg;base64,${data[currentTrackIndex].image.data}` : "",
      });
    }
  };

 

  const playNextTrack = () => {
    if (currentTrackIndex === audioFiles.length - 1) return;
    const nextIndex = currentTrackIndex + 1;
    setCurrentTrackIndex(nextIndex);
  };

  const playPreviousTrack = () => {
    if (currentTrackIndex === 0) return;
    const previousIndex = currentTrackIndex - 1;
    setCurrentTrackIndex(previousIndex);
  };

  return (
    <AudioContext.Provider
      value={{
        currentTrack,
        setCurrentTrack,
        isPlaying,
        setIsPlaying,
        volume,
        setVolume,
        audioFiles,
        updateAudioFiles,
        currentTrackIndex,
        setCurrentTrackIndex,
        audioRef,
        playNextTrack,
        playPreviousTrack,
      }}
    >
      {children}
    </AudioContext.Provider>
  );
};

AudioProvider.propTypes = {
  children: PropTypes.node.isRequired,
};
