import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import AudioList from './components/audioList/AudioList';
import UploadForm from './components/uploadAudio/UploadForm';
import Navbar from './components/Navbar';
import AudioControls from './components/audioControls/AudioControls';
import AudioContext from './AudioContext';

function App() {
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);

  return (
    <AudioContext.Provider value={{ currentTrack, setCurrentTrack, isPlaying, setIsPlaying, volume, setVolume }}>
      <div className="App">
        <Router>
          <Navbar />
          <Routes>
            <Route path="/audio/upload" element={<UploadForm />} />
            <Route path="/audio" element={<AudioList />} />
          </Routes>
          <AudioControls />
        </Router>
      </div>
    </AudioContext.Provider>
  );
}

export default App;
