import React, { useState, useEffect } from "react";

const AudioList = () => {
  const [audioFiles, setAudioFiles] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => setAudioFiles(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handlePlayAudio = async (audioId, trackName, author, imageUrl) => {
    try {
      const response = await fetch(`http://localhost:8080/audio/${audioId}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const blob = await response.blob();
      const audioData = URL.createObjectURL(blob);
      setCurrentTrack({ audioUrl: audioData, trackName, author, imageUrl });
    } catch (error) {
      console.error("Error fetching audio:", error);
    }
  };

  return (
    <div>
      <h1>Play Audio Files</h1>
      <ul style={{ listStyleType: "none", padding: 0 }}>
        {audioFiles.map((audioFile) => (
          <li key={audioFile.id} style={{ display: "flex", alignItems: "center", marginBottom: "10px" }}>
            <div style={{ marginRight: "10px" }}>
              {audioFile.image && (
                <img
                  src={`data:image/jpeg;base64,${audioFile.image.data}`}
                  alt={audioFile.title}
                  style={{ maxWidth: "50px", maxHeight: "50px" }}
                />
              )}
            </div>
            <div style={{ display: "flex", alignItems: "center" }}>
              <button
                onClick={() =>
                  handlePlayAudio(
                    audioFile.id,
                    audioFile.title,
                    audioFile.author,
                    `data:image/jpeg;base64,${audioFile.image.data}`
                  )
                }
                style={{ marginRight: "10px", padding: "5px 10px", backgroundColor: "#007BFF", color: "white", border: "none", borderRadius: "4px", cursor: "pointer" }}
              >
                Play
              </button>
              <span style={{ fontSize: "14px" }}>
                {audioFile.title} - {audioFile.author}
              </span>
            </div>
          </li>
        ))}
      </ul>

      {/* Кастомный Controls */}
      {currentTrack && (
        <div id="customControls" style={{ backgroundColor: "#f0f0f0", padding: "10px", borderRadius: "4px", marginTop: "20px", display: "flex", alignItems: "center", position: "fixed", bottom: 0, left: 0, right: 0 }}>
          <div style={{ display: "flex", alignItems: "center", marginRight: "10px" }}>
            <img
              id="audioImage2"
              src={currentTrack.imageUrl}
              style={{ maxWidth: "50px", maxHeight: "50px", marginRight: "10px" }}
            />
            <div>
              <span id="customTrackName" style={{ fontSize: "16px", fontWeight: "bold" }}>{currentTrack.trackName}</span>
              <br />
              <span id="customAuthor" style={{ fontSize: "14px", color: "#6c757d" }}>{currentTrack.author}</span>
            </div>
          </div>
          <audio
            id="customAudioPlayer"
            src={currentTrack.audioUrl}
            controls
            style={{ width: "100%" }}
            type="audio/mpeg"
          >
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
    </div>
  );
};

export default AudioList;
