import React, { useState, useEffect } from "react";

const AudioList = () => {
  const [audioFiles, setAudioFiles] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => setAudioFiles(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handleAudioControls = () => {
    // Обработка кликов и воспроизведения аудиозаписей
  };

  const listStyle = {
    listStyleType: "none",
    padding: 0,
    margin: 0
  };

  const liStyle = {
    margin: "10px 0"
  };

  const controlWrapStyle = {
    textAlign: "center"
  };

  // Добавление стилей из <style> тега
  const controlStyle = {
    border: "11px solid #337AB7",
    borderRadius: "50%",
    margin: "20px",
    padding: "28px",
    width: "112px",
    height: "112px",
    fontSize: 0,
    whiteSpace: "nowrap",
    textAlign: "center",
    cursor: "pointer"
  };

  return (
    <div style={controlWrapStyle}>
      <h1>Play Audio Files</h1>
      <ul style={listStyle}>
        {audioFiles.map((audioFile) => (
          <li key={audioFile.id} style={liStyle}>
            <div>
              <div>
                {audioFile.image && (
                  <img
                    src={`data:image/jpeg;base64,${audioFile.image.data}`}
                    alt={audioFile.title}
                    style={{ maxWidth: "50px", maxHeight: "50px" }}
                  />
                )}
              </div>
              <div className="control-wrap">
                <div
                  className="control play"
                  style={controlStyle}
                >
                  <span className="left"></span>
                  <span className="right"></span>
                  <span>{audioFile.author} - {audioFile.title}</span>
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AudioList;
