import React, { useState, useEffect } from "react";

const AudioList = () => {
  const [audioFiles, setAudioFiles] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => setAudioFiles(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  return (
    <div>
      <h1>Play Audio Files</h1>
      <ul>
        {audioFiles.map((audioFile) => (
          <li key={audioFile.id}>
            <div>
              <div>
                <img
                  src={`http://localhost:8080/audio/${audioFile.id}/image`}
                  alt={audioFile.title}
                  style={{ maxWidth: "50px", maxHeight: "50px" }}
                />
              </div>
              <div>
              <div>
                  <span className="left"></span>
                  <span className="right"></span>
                  <span>{audioFile.author} - {audioFile.title}</span>
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

useEffect(() => {
  const customAudioPlayer = document.getElementById('customAudioPlayer');
  const audioImage = document.getElementById('audioImage2');
  const customTrackName = document.getElementById('customTrackName');
  const customControls = document.getElementById('customControls');

  document.addEventListener('DOMContentLoaded', function () {
    let currentAudioFileId = null;
    let currentAudioPlayer = null;

    // Get all elements with the class .control
    const controlElements = $('.control');
    const customAudioPlayer = $('#customAudioPlayer');
    const audioImage = $('#audioImage2');
    const customTrackName = $('#customTrackName');
    const customControls = $('#customControls');

    // Add a click event handler to each .control element
    controlElements.on('click', function () {
        const clickedAudioFileId = $(this).data('audio-id');

        // If a different audio file is clicked, pause the current one
        if (clickedAudioFileId !== currentAudioFileId) {
            pauseAllAudios();
            playAudio($(this));
        } else {
            // Toggle play/pause for the clicked audio file
            if (customAudioPlayer[0].paused) {
                customAudioPlayer[0].play();
            } else {
                customAudioPlayer[0].pause();
            }
        }
    });

    function playAudio(buttonElement) {
        const audioFileId = buttonElement.data('audio-id');
        const audioAuthor = buttonElement.data('audio-author');
        const audioTitle = buttonElement.data('audio-title');

        const src = '/audio/' + audioFileId;
        customAudioPlayer.attr('src', src);
        audioImage.attr('src', src + '/image');

        customTrackName.text(audioAuthor + ' - ' + audioTitle);

        customAudioPlayer[0].play();

        customControls.css('display', 'block');

        // Update the current audio file ID and player reference
        currentAudioFileId = audioFileId;
        currentAudioPlayer = customAudioPlayer[0];
    }

    function pauseAllAudios() {
        // Pause all audio players
        controlElements.each(function () {
            const audioPlayerId = $(this).data('audio-id');
            const audioPlayer = $('#audioPlayer_' + audioPlayerId)[0];
            if (audioPlayer && !audioPlayer.paused) {
                audioPlayer.pause();
            }
        });
    }
});

$('.control').on('click', function () {
    $(this).toggleClass('pause play');
});

  return () => {
    // Очистка после размонтирования компонента, если необходимо
  };
}, []);

export default AudioList;
