import React, { useState, useRef } from "react";
import AudioList from "./components/audioList/AudioList";
import UploadForm from "./components/uploadAudio/UploadForm";
import Navbar from "./components/Navbar";
import AudioControls from "./components/audioControls/AudioControls";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

function App() {
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [nextTrack, setNextTrack] = useState(false);
  const audioRef = useRef(null);
  const [volume, setVolume] = useState(1);
   const [showAudioControls, setShowAudioControls] = useState(true);

  const toggleAudioControls = () => {
    setShowAudioControls(!showAudioControls);
  };

  return (
    <div className="App">
      <Router>
        <Navbar />
        <Routes>
          <Route path="/audio/upload" element={<UploadForm />} />
          <Route path="/audio" element={<AudioList
                setCurrentTrack={setCurrentTrack}
                setIsPlaying={setIsPlaying}
                setNextTrack={setNextTrack}
                audioRef={audioRef}
                setVolume={setVolume}
              />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
