import React, { useContext } from 'react';
import { AudioContext } from '../../App';

const AudioControls = () => {
  const { currentTrack, isPlaying, volume, setIsPlaying, setVolume } = useContext(AudioContext);
  const audioRef = useRef(null);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = volume;
    }
  }, [volume]);

  useEffect(() => {
    if (audioRef.current) {
      if (isPlaying) {
        audioRef.current.play();
      } else {
        audioRef.current.pause();
      }
    }
  }, [isPlaying]);

  const handleVolumeChange = (e) => {
    setVolume(e.target.value);
    if (audioRef.current) {
      audioRef.current.volume = e.target.value;
    }
  };

  const togglePlayPause = () => {
    setIsPlaying(!isPlaying);
  };

  return (
    <div className="audio-controls">
      <div className="custom-controls">
        <div className="meta-data">
          <img
            id="audioImage2"
            src={currentTrack ? currentTrack.imageUrl : "../music.jpg"}
            alt="Track artwork"
          />
          <div className="title-author">
            <span className="title">{currentTrack ? currentTrack.trackName : ""}</span>
            <span className="author">{currentTrack ? currentTrack.author : ""}</span>
          </div>
        </div>
        <div className="play-pause-container">
          <button className="play_pause" onClick={togglePlayPause}>
            {isPlaying ? "||" : ">"}
          </button>
        </div>
        <audio
          id="customAudioPlayer"
          ref={audioRef}
          src={currentTrack ? currentTrack.audioUrl : ""}
          type="audio/mpeg"
          onEnded={() => setIsPlaying(false)} // Callback when audio ends
        >
          Your browser does not support the audio element.
        </audio>
        <div className="volume-container">
          <input
            className="volume"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={handleVolumeChange}
          />
        </div>
      </div>
    </div>
  );
};

export default AudioControls;
