import React, { useEffect, useRef } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioControls.css";

const AudioControls = () => {
  const {
    currentTrack,
    isPlaying,
    togglePlay,
    volume,
    audioRef,
    setVolume,
    debouncedPlayNextTrack,
    debouncedPlayPreviousTrack,
    currentTrackIndex,
    audioFiles,
  } = useAudioContext();
  const prevAudioUrl = useRef(null);

  useEffect(() => {
    if (currentTrack) {
      audioRef.current.src = currentTrack.audioUrl;
    }
  }, [currentTrack]);

  const handleTogglePlay = () => {
    if (currentTrackIndex !== -1) {
      if (isPlaying) {
        audioRef.current.pause(); // Пауза воспроизведения
      } else {
        audioRef.current.play(); // Возобновление воспроизведения
      }
      togglePlay(); // Переключение состояния воспроизведения
    }
  };

  useEffect(() => {
    audioRef.current.volume = volume;
  }, [volume]);

  return (
    <div className="audio-controls">
      <div className="custom-controls">
        <div className="meta-data">
          {currentTrack ? (
            <>
              <img
                id="audioImage2"
                src={currentTrack.imageUrl}
                alt={currentTrack.trackName}
              />
              <div className="title-author">
                <span className="title">{currentTrack.trackName}</span>
                <span className="author">{currentTrack.author}</span>
              </div>
            </>
          ) : (
            <>
              <img id="audioImage2" src="note.png" alt="Track" />
              <div className="title-author">
                <span className="title">Track Name</span>
                <span className="author">Author</span>
              </div>
            </>
          )}
        </div>
        <div className="controls-container">
          <button
            className="previous"
            onClick={debouncedPlayPreviousTrack}
            disabled={!audioFiles.length || currentTrackIndex === 0}
          >
            <i className="fas fa-backward"></i>
          </button>
          <button className="play-pause" onClick={handleTogglePlay}>
            {currentTrackIndex !== -1 && isPlaying ? "||" : ">"}
          </button>
          <button
            className="next"
            onClick={debouncedPlayNextTrack}
            disabled={
              !audioFiles.length || currentTrackIndex === audioFiles.length - 1
            }
          >
            <i className="fas fa-forward"></i>
          </button>
        </div>
        <audio
          id="customAudioPlayer"
          ref={audioRef}
          controls
          autoPlay={isPlaying}
          type="audio/mpeg"
        >
          Your browser does not support the audio element.
        </audio>
        <div></div>

        <div className="volume-container">
          <input
            className="volume"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={(e) => setVolume(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
};

export default AudioControls;
