import React, { useEffect, useRef, useState } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioControls.css";

const AudioControls = () => {
  const {
    currentTrack,
    isPlaying,
    togglePlay,
    volume: contextVolume,
    audioRef,
    debouncedPlayNextTrack,
    debouncedPlayPreviousTrack,
    currentTrackIndex,
    audioFiles,
    setVolume, // Добавляем setVolume из контекста
  } = useAudioContext();
  const [volume, setLocalVolume] = useState(contextVolume); // Локальное состояние громкости
  const prevAudioUrl = useRef(null);

  useEffect(() => {
    if (currentTrack) {
      audioRef.current.src = currentTrack.audioUrl;
      audioRef.current.volume = contextVolume; // Используем значение громкости из контекста
      setLocalVolume(contextVolume); // Обновляем локальное состояние громкости
    }
  }, [currentTrack, contextVolume]); // Обновляем при изменении текущего трека или громкости из контекста

  const handleTogglePlay = () => {
    if (isPlaying) {
      audioRef.current.pause();
    } else {
      audioRef.current.play();
    }
    togglePlay();
  };

  const handleVolumeChange = (e) => {
    const newVolume = parseFloat(e.target.value);
    setLocalVolume(newVolume); // Обновляем локальное состояние громкости
    setVolume(newVolume); // Обновляем громкость в контексте
  };

  return (
    <div className="audio-controls">
      <div className="custom-controls">
        <div className="meta-data">
          {currentTrack ? (
            <>
              <img
                id="audioImage2"
                src={currentTrack.imageUrl}
                alt={currentTrack.trackName}
              />
              <div className="title-author">
                <span className="title">{currentTrack.trackName}</span>
                <span className="author">{currentTrack.author}</span>
              </div>
            </>
          ) : (
            <>
              <img id="audioImage2" src="" alt="Track" />
              <div className="title-author">
                <span className="title">Track Name</span>
                <span className="author">Author</span>
              </div>
            </>
          )}
        </div>
        <div className="controls-container">
          <button
            className="previous"
            onClick={debouncedPlayPreviousTrack}
            disabled={!audioFiles.length || currentTrackIndex === 0}
          >
             <i className="fas fa-backward"></i>
          </button>
          <button className="play-pause" onClick={handleTogglePlay}>
          {isPlaying ? "||" : ">"}
          </button>
          <button
            className="next"
            onClick={debouncedPlayNextTrack}
            disabled={
              !audioFiles.length || currentTrackIndex === audioFiles.length - 1
            }
          >
            <i className="fas fa-forward"></i>
          </button>
        </div>
        <audio
          id="customAudioPlayer"
          ref={audioRef}
          controls
          autoPlay={isPlaying}
          type="audio/mpeg"
        >
          Your browser does not support the audio element.
        </audio>

        <div className="volume-container">
          <input
            className="volume"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={handleVolumeChange}
          />
        </div>
      </div>
    </div>
  );
};

export default AudioControls;
