import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { useAudioContext } from "../AudioContext"; // Импортируем контекст AudioContext
import "./PlaylistContainer.css";

const PlaylistContainer = () => {
  const [playlists, setPlaylists] = useState([]);
  const { clearAudioFiles, id } = useAudioContext(); // Получаем функцию clearAudioFiles из контекста

  useEffect(() => {
    // Здесь вы можете выполнить запрос к серверу для получения списка плейлистов
    // Пример:
    fetch("http://localhost:8080/api/playlists")
      .then((response) => response.json())
      .then((data) => setPlaylists(data))
      .catch((error) => console.error("Error fetching playlists:", error));

    // Обнуляем содержимое AudioList при монтировании компонента
    clearAudioFiles();
  }, [clearAudioFiles]);

  return (
    <div className="playlist-container">
      <h2>Playlists</h2>
      <div className="playlist-list">
        {playlists.map((playlist) => (
          <div
            key={playlist.id}
            style={{
              width: "200px",
              height: "200px",
              border: "black solid 2px",
              borderRadius: "10px",
            }}
          >
            <Link to={`/playlists/${playlist.id}`}>
              <div className="playlist-item">
                <h3>{playlist.name}</h3>
                <p>Author: {playlist.author}</p>
                <p>Tracks: {playlist.countOfAudio}</p>
                <p>Duration: {playlist.duration}</p>
                {/* Здесь можно добавить другие детали плейлиста */}
              </div>
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PlaylistContainer;
