import React, { useState, createContext } from 'react';
import AudioList from './components/audioList/AudioList';
import UploadForm from './components/uploadAudio/UploadForm';
import Navbar from './components/Navbar';
import AudioControls from './components/audioControls/AudioControls';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

// Создаем контекст
export const AudioContext = createContext();

function App() {
  // Состояние для AudioControls
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);

  // Объект состояния для передачи через контекст
  const audioState = {
    currentTrack,
    setCurrentTrack,
    isPlaying,
    setIsPlaying,
    volume,
    setVolume,
  };

  return (
    <AudioContext.Provider value={audioState}>
      <div className="App">
        <Router>
          <Navbar />
          <Routes>
            <Route path="/audio/upload" element={<UploadForm />} />
            <Route path="/audio" element={<AudioList />} />
          </Routes>
          <AudioControls />
        </Router>
      </div>
    </AudioContext.Provider>
  );
}

export default App;
