import React, { useEffect, useRef, useState } from "react";
import { useParams } from "react-router-dom";
import { useAudioContext } from "../AudioContext";
import "./AudioControls.css";

const AudioControls = () => {
  const {
    currentTrack,
    isPlaying,
    togglePlay,
    volume,
    audioRef,
    setVolume,
    debouncedPlayNextTrack,
    debouncedPlayPreviousTrack,
    currentTrackIndex,
    audioFiles,
    currentPlaylistId,
    setCurrentPlaylistId,
    playlistId,
    setPlaylistId,
  } = useAudioContext();
  const [currentTime, setCurrentTime] = useState(0);

  const prevAudioUrl = useRef(null);

  useEffect(() => {
    // Запрос и обновление данных выполняются только при начале воспроизведения аудио
    if (currentTrackIndex !== -1 && currentPlaylistId !== playlistId) {
      // Если текущий плейлист изменился, но воспроизведение не начато
      setCurrentPlaylistId(id); // Обновляем текущий плейлист
    }
  }, [currentTrackIndex, currentPlaylistId, id]);

  useEffect(() => {
    const updateTime = () => {
      setCurrentTime(audioRef.current.currentTime);
    };

    const interval = setInterval(updateTime, 100); // Обновляем текущее время каждые 100 миллисекунд

    return () => clearInterval(interval);
  }, [audioRef]);

  useEffect(() => {
    if (currentTrack) {
      audioRef.current.src = currentTrack.audioUrl;
    }
  }, [currentTrack]);

  const handleTogglePlay = () => {
    if (currentTrackIndex !== -1) {
      if (isPlaying) {
        audioRef.current.pause(); // Пауза воспроизведения
      } else {
        audioRef.current.play(); // Возобновление воспроизведения
      }
      togglePlay(); // Переключение состояния воспроизведения
    }
  };

  useEffect(() => {
    audioRef.current.volume = volume;
  }, [volume]);

  useEffect(() => {
    const handleAudioEnded = () => {
      // Проверяем, является ли текущий трек последним в списке треков
      if (currentTrackIndex === audioFiles.length - 1) {
        // Если текущий трек последний, ставим проигрывание на паузу
        togglePlay();
      } else {
        // Если текущий трек не последний, включаем следующий трек
        debouncedPlayNextTrack();
      }
    };

    audioRef.current.addEventListener("ended", handleAudioEnded);

    return () => {
      audioRef.current.removeEventListener("ended", handleAudioEnded);
    };
  }, [
    debouncedPlayNextTrack,
    togglePlay,
    audioRef,
    currentTrackIndex,
    audioFiles,
  ]);

  function formatDuration(duration) {
    const hours = Math.floor(duration / 3600);
    const minutes = Math.floor((duration % 3600) / 60);
    const seconds = Math.floor(duration % 60);

    let formattedTime = "";
    if (hours > 0) {
      formattedTime += hours + ":";
    }
    if (hours > 0 && minutes < 10) {
      formattedTime += "0";
    }
    formattedTime += String(minutes).padStart(hours > 0 ? 2 : 1, "0") + ":";
    formattedTime += String(seconds).padStart(2, "0");

    return formattedTime;
  }

  return (
    <div className="audio-controls">
      <div className="custom-controls">
        <div className="meta-data">
          {currentTrack ? (
            <>
              <img
                id="audioImage2"
                src={currentTrack.imageUrl}
                alt={currentTrack.trackName}
              />
              <div className="title-author">
                <span className="title">{currentTrack.trackName}</span>
                <span className="author">{currentTrack.author}</span>
              </div>
            </>
          ) : (
            <>
              <img id="audioImage2" src="note.png" alt="Track" />
              <div className="title-author">
                <span className="title">Track Name</span>
                <span className="author">Author</span>
              </div>
            </>
          )}
        </div>
        <div className="controls-container">
          <button
            className="previous"
            onClick={debouncedPlayPreviousTrack}
            disabled={!audioFiles.length || currentTrackIndex === 0}
          >
            <i className="fas fa-backward"></i>
          </button>
          <button className="play-pause" onClick={handleTogglePlay}>
            {currentTrackIndex !== -1 && isPlaying ? "||" : ">"}
          </button>
          <button
            className="next"
            onClick={debouncedPlayNextTrack}
            disabled={
              !audioFiles.length || currentTrackIndex === audioFiles.length - 1
            }
          >
            <i className="fas fa-forward"></i>
          </button>
        </div>
        <div className="timeline-container">
          <div className="current-time">
            <span>{currentTrack ? formatDuration(currentTime) : "0:00"}</span>
          </div>
          <audio
            id="customAudioPlayer"
            ref={audioRef}
            controls
            autoPlay={isPlaying}
            type="audio/mpeg"
          >
            Your browser does not support the audio element.
          </audio>
          <div className="duration">
            <span>
              {currentTrack ? formatDuration(currentTrack.duration) : "0:00"}
            </span>
          </div>
        </div>
        <div className="volume-container">
          <input
            className="volume"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={(e) => setVolume(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
};

export default AudioControls;
