import React, { useEffect, useRef } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioControls.css";

const AudioControls = () => {
  const {
    currentTrack,
    isPlaying,
    volume,
    audioFiles,
    setCurrentTrack,
    setIsPlaying,
    nextTrack,
    setNextTrack,
    setVolume,
  } = useAudioContext();
  const audioRef = useRef(null);

  useEffect(() => {
    const { setCurrentTrack, setNextTrack } = useAudioContext(); // Деструктурируем setCurrentTrack и setNextTrack здесь
    if (audioRef.current) {
      audioRef.current.volume = volume;
      audioRef.current.addEventListener("ended", () => {
        if (nextTrack) {
          setCurrentTrack(nextTrack);
          setNextTrack(null);
        }
      });
    }
  }, [audioFiles, currentTrack, volume, nextTrack]); // Добавляем nextTrack в список зависимостей

  return (
    <div className="audio-controls">
      {currentTrack && (
        <div className="custom-controls">
          <div className="meta-data">
            <img
              id="audioImage2"
              src={currentTrack.imageUrl || "../music.jpg"}
              alt={currentTrack.trackName}
            />
            <div className="title-author">
              <span className="title">{currentTrack.trackName}</span>
              <span className="author">{currentTrack.author}</span>
            </div>
          </div>
          <audio
            id="customAudioPlayer"
            ref={audioRef}
            src={currentTrack.audioUrl}
            controls
            autoPlay={isPlaying}
            type="audio/mpeg"
          >
            Your browser does not support the audio element.
          </audio>
          <div className="volume-container">
            <input
              className="volume"
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={(e) => setVolume(e.target.value)}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default AudioControls;
