import React, { useState, useEffect, useRef } from "react";

const AudioList = () => {
  const [audioFiles, setAudioFiles] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [nextTrack, setNextTrack] = useState(false);
  const audioRef = useRef(null);
  const [volume, setVolume] = useState(1);

  useEffect(() => {
    const cachedAudioFiles = localStorage.getItem('cachedAudioFiles');

    if (cachedAudioFiles) {
      setAudioFiles(JSON.parse(cachedAudioFiles));
    } else {
      fetch("http://localhost:8080/audio", { method: "GET" })
        .then((response) => response.json())
        .then((data) => {
          const audioFiles = data.map((file) => ({ name: file.name, size: file.size }));
          setAudioFiles(audioFiles);
          localStorage.setItem('cachedAudioFiles', JSON.stringify(audioFiles));
        })
        .catch((error) => console.error("Error fetching data:", error));
    }
  }, []);

  useEffect(() => {
    const fetchData = async () => {
        try {
            const response = await fetch("http://localhost:8080/audio");
            const data = await response.json();
            setAudioFiles(data);
        } catch (error) {
            console.error("Error fetching data:", error);
        }
    };

    fetchData();
}, []);

  const handlePlayAudio = async (audioId, trackName, author, imageUrl) => {
    if (currentTrack && currentTrack.id === audioId) {
        setIsPlaying(!isPlaying);
        if (isPlaying) {
            audioRef.current.pause();
        } else {
            audioRef.current.play();
        }
    } else {
        try {
            const response = await fetch(`http://localhost:8080/audio/${audioId}`);
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const blob = await response.blob();
            const audioData = URL.createObjectURL(new Blob([blob]));

            // Пробуем предварительно загрузить следующий трек
            const nextIndex = audioFiles.findIndex(file => file.id === audioId) + 1;
            if (nextIndex < audioFiles.length) {
                const nextResponse = await fetch(`http://localhost:8080/audio/${audioFiles[nextIndex].id}`);
                if (!nextResponse.ok) {
                    throw new Error(`HTTP error! status: ${nextResponse.status}`);
                }
                const nextBlob = await nextResponse.blob();
                const nextAudioData = URL.createObjectURL(new Blob([nextBlob]));

                // Предварительно сохраняем следующий трек для использования при окончании текущего
                setNextTrack({
                    id: audioFiles[nextIndex].id,
                    audioUrl: nextAudioData,
                    trackName: audioFiles[nextIndex].title,
                    author: audioFiles[nextIndex].author,
                    imageUrl: `data:image/jpeg;base64,${audioFiles[nextIndex].image.data}`
                });
            }

            setCurrentTrack({
                id: audioId,
                audioUrl: audioData,
                trackName,
                author,
                imageUrl
            });
            setIsPlaying(true);
        } catch (error) {
            console.error("Error fetching audio:", error);
        }
    }
};

useEffect(() => {
    if (audioRef.current) {
        audioRef.current.volume = volume;
        audioRef.current.addEventListener('ended', () => {
            if (nextTrack) {
                setCurrentTrack(nextTrack);
                setNextTrack(null); // Сбрасываем следующий трек после использования
            } 
        });
    }
}, [audioFiles, currentTrack, handlePlayAudio, volume, nextTrack]);


  return (
    <div
      style={{
        borderRadius: "10px",
        backgroundColor: "#ffffff",
        padding: "20px",
        width: "400px",
        display: "flex",
        margin: "0 auto",
      }}
    >
      <ul style={{ listStyleType: "none", padding: 0 }}>
        {audioFiles.map((audioFile) => (
          <li
            key={audioFile.id}
            style={{
              display: "flex",
              alignItems: "center",
              marginBottom: "10px",
            }}
          >
            <div
              style={{
                marginRight: "10px",
                display: "flex",
                alignItems: "center",
                border: "1px solid grey",
                borderRadius: "4px",
                padding: "7px",
              }}
            >
              {audioFile.image && (
                <img
                  src={`data:image/jpeg;base64,${audioFile.image.data}`}
                  alt={audioFile.title}
                  style={{
                    width: "50px",
                    height: "50px",
                    marginRight: "10px",
                  }}
                />
              )}
              <div
                style={{
                  width: "50px",
                }}
              >
                <button
                  onClick={() =>
                    handlePlayAudio(
                      audioFile.id,
                      audioFile.title,
                      audioFile.author,
                      `data:image/jpeg;base64,${audioFile.image.data}`
                    )
                  }
                  style={{
                    marginRight: "10px",
                    padding: "5px 10px",
                    backgroundColor: "#b9b5ff",
                    color: "white",
                    fontSize: "20px",
                    fontWeight: "bold",
                    border: "none",
                    borderRadius: "4px",
                    cursor: "pointer",
                    height: "40px",
                    width: "40px",
                    borderRadius: "100px",
                  }}
                >
                  {currentTrack && currentTrack.id === audioFile.id && isPlaying
                    ? "||"
                    : ">"}
                </button>
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  width: "220px",
                }}
              >
                <span
                  style={{
                    fontSize: "18px",
                    fontWeight: "bold",
                    marginBottom: "5px",
                  }}
                >
                  {audioFile.title}
                </span>
                <span style={{ fontSize: "16px", color: "#6c757d" }}>
                  {audioFile.author}
                </span>
              </div>
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  marginRight: "20px",
                }}
              >
                <span
                  style={{
                    fontSize: "18px",
                    color: "grey",
                    fontWeight: "lighter",
                    marginBottom: "5px",
                  }}
                >
                  {audioFile.duration}
                </span>
              </div>
            </div>
          </li>
        ))}
      </ul>

      {/* Кастомный Controls */}
      {currentTrack && (
        <div
          id="customControls"
          style={{
            backgroundColor: "#f0f0f0",
            padding: "10px",
            borderRadius: "4px",
            position: "fixed",
            bottom: 0,
            left: 0,
            right: 0,
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            boxSizing: "border-box",
            width: "100%",
            margin: "0 auto",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <img
              id="audioImage2"
              src={currentTrack.imageUrl}
              style={{
                width: "70px",
                height: "70px",
                marginRight: "10px",
              }}
            />
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                marginLeft: "10px",
                width: "200px",
              }}
            >
              <span
                id="customTrackName"
                style={{
                  fontSize: "20px",
                  fontWeight: "bold",
                  marginBottom: "5px",
                }}
              >
                {currentTrack.trackName}
              </span>
              <span
                id="customAuthor"
                style={{ fontSize: "18px", color: "#6c757d" }}
              >
                {currentTrack.author}
              </span>
            </div>
          </div>
          <audio
            id="customAudioPlayer"
            ref={audioRef}
            src={currentTrack.audioUrl}
            controls
            autoPlay={isPlaying}
            style={{ width: "1200px", marginRight: "130px" }}
            type="audio/mpeg"
          >
            Your browser does not support the audio element.
          </audio>
          <div style={{ display: "flex", alignItems: "center" }}>
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={(e) => setVolume(e.target.value)}
              style={{
                width: "100px",
                marginRight: "100px", 
              }}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default AudioList;
