import React, { useEffect, useRef } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioControls.css";

const AudioControls = ({
  currentTrack,
  isPlaying,
  volume,
  audioRef,
  setVolume,
}) => {
  return (
    <div className="audio-controls">
      <div className="custom-controls">
        <div className="meta-data">
          {currentTrack ? (
            <>
              <img id="audioImage2" src={currentTrack.imageUrl} />
              <div className="title-author">
                <span className="title">{currentTrack.trackName}</span>
                <span className="author">{currentTrack.author}</span>
              </div>
            </>
          ) : (
            <>
              <img id="audioImage2" src="" />
              <div className="title-author">
                <span className="title">Track Name</span>
                <span className="author">Author</span>
              </div>
            </>
          )}
        </div>
        <audio
          id="customAudioPlayer"
          ref={audioRef}
          src={currentTrack ? currentTrack.audioUrl : ""}
          controls
          autoPlay={isPlaying}
          type="audio/mpeg"
        >
          Your browser does not support the audio element.
        </audio>
        <div className="volume-container">
          <input
            className="volume"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={(e) => setVolume(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
};

AudioControls.defaultProps = {
  currentTrack: null,
  isPlaying: false,
  volume: 0,
  audioRef: null,
  setVolume: () => {}
};

export default AudioControls;
