import React, { useEffect, useRef } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioControls.css";

const AudioControls = () => {
  const { currentTrack, isPlaying, volume, setVolume, audioRef, audioFiles, currentTrackIndex, setCurrentTrackIndex, setCurrentTrack } = useAudioContext();

  useEffect(() => {
    const audioElement = audioRef.current;

    const handleEnded = async () => {
      // Проверяем, есть ли следующий трек в списке
      if (currentTrackIndex < audioFiles.length - 1) {
        const nextTrackIndex = currentTrackIndex + 1;
        const nextTrack = audioFiles[nextTrackIndex];
        // const nextAudioData = `data:image/jpeg;base64,${nextTrack.image.data}`;

        const nextResponse = await fetch(
          `http://localhost:8080/audio/${audioFiles[nextIndex].id}`
        );
        if (!nextResponse.ok) {
          throw new Error(`HTTP error! status: ${nextResponse.status}`);
        }
        const nextBlob = await nextResponse.blob();
        const nextAudioData = URL.createObjectURL(new Blob([nextBlob]));


        setCurrentTrackIndex(nextTrackIndex);
        setCurrentTrack({
          id: audioFiles[nextIndex].id,
            audioUrl: nextAudioData,
            trackName: audioFiles[nextIndex].title,
            author: audioFiles[nextIndex].author,
            imageUrl: `data:image/jpeg;base64,${audioFiles[nextIndex].image.data}`,
        });
        
        const nextIndex =
          audioFiles.findIndex((file) => file.id === audioId) + 1;
        if (nextIndex < audioFiles.length) {
          const nextResponse = await fetch(
            `http://localhost:8080/audio/${audioFiles[nextIndex].id}`
          );
          if (!nextResponse.ok) {
            throw new Error(`HTTP error! status: ${nextResponse.status}`);
          }
          const nextBlob = await nextResponse.blob();
          const nextAudioData = URL.createObjectURL(new Blob([nextBlob]));

          // Предварительно сохраняем следующий трек для использования при окончании текущего
        }
        // Воспроизводим следующий трек
        audioElement.play();
      } else {
        // Если достигнут конец списка, останавливаем воспроизведение
        setCurrentTrack(null);
        setCurrentTrackIndex(-1);
        audioElement.pause();
      }
    };

    audioElement.addEventListener("ended", handleEnded);

    return () => {
      audioElement.removeEventListener("ended", handleEnded);
    };
  }, [currentTrackIndex]);

  return (
    <div className="audio-controls">
      <div className="custom-controls">
        <div className="meta-data">
          {currentTrack ? (
            <>
              <img id="audioImage2" src={currentTrack.imageUrl} alt="track cover" />
              <div className="title-author">
                <span className="title">{currentTrack.trackName}</span>
                <span className="author">{currentTrack.author}</span>
              </div>
            </>
          ) : (
            <>
              <img id="audioImage2" src="" alt="track cover" />
              <div className="title-author">
                <span className="title">Track Name</span>
                <span className="author">Author</span>
              </div>
            </>
          )}
        </div>
        <audio
          id="customAudioPlayer"
          ref={audioRef}
          src={currentTrack ? currentTrack.audioUrl : ""}
          controls
          autoPlay={isPlaying}
          type="audio/mpeg"
        >
          Your browser does not support the audio element.
        </audio>
        <div className="volume-container">
          <input
            className="volume"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={(e) => setVolume(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
};

export default AudioControls;
