import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import AudioList from "./components/audioList/AudioList";
import UploadForm from "./components/uploadAudio/UploadForm";
import Navbar from "./components/navbar/Navbar";
import { AudioProvider } from "./components/AudioContext";
import AudioControls from "./components/audioControls/AudioControls";
import PlaylistContainer from "./components/playlistContainer/PlaylistContainer";

function App() {
  const showMainControls = false; // Флаг для отображения основных контролов

  const [currentPlaylistId, setCurrentPlaylistId] = useState(-2);
  const [playlistId, setPlaylistId] = useState(-1);

  return (
    <div className="App">
      <Router>
        <Navbar />
        <AudioContext.Provider value={{ currentPlaylistId, setCurrentPlaylistId, playlistId, setPlaylistId }}>
          <AudioControls />
          <Routes>
            <Route path="/audio/upload" element={<UploadForm />} />
            {/* <Route path="/audio" element={<AudioList />} /> */}
            <Route exact path="/playlists/:id" element={<AudioList />} />
            <Route path="/playlists" element={<PlaylistContainer />} />
          </Routes>
        </AudioContext.Provider>
      </Router>
    </div>
  );
}

export default App;
