import React, { useEffect, useRef } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioControls.css";

const AudioControls = ({ audioRef }) => {
  const {
    currentTrack,
    isPlaying,
    volume,
    setVolume,
    setCurrentTrack,
    setNextTrack,
  } = useAudioContext();

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = volume;
      audioRef.current.src = currentTrack.audioUrl;
      if (isPlaying) {
        audioRef.current.play();
      } else {
        audioRef.current.pause();
      }
    }
  }, [currentTrack, isPlaying, volume, audioRef]);

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.addEventListener("ended", () => {
        setCurrentTrack(null);
        setNextTrack(null);
      });
    }
  }, [audioRef, setCurrentTrack, setNextTrack]);

  return (
    <div className="audio-controls">
      {currentTrack && (
        <div className="custom-controls">
          <div className="meta-data">
            <img
              id="audioImage2"
              src={currentTrack.imageUrl || "../music.jpg"}
              alt={currentTrack.trackName}
            />
            <div className="title-author">
              <span className="title">{currentTrack.trackName}</span>
              <span className="author">{currentTrack.author}</span>
            </div>
          </div>
          <audio
            id="customAudioPlayer"
            ref={audioRef}
            controls
            autoPlay={isPlaying}
            type="audio/mpeg"
          >
            Your browser does not support the audio element.
          </audio>
          <div className="volume-container">
            <input
              className="volume"
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={(e) => setVolume(e.target.value)}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default AudioControls;
