import React, { useState, useEffect, useRef } from "react";
import "./AudioControls.css";

const AudioControls = ({
  isWork,
  currentTrack,
  isPlaying,
  volume,
  audioRef,
  setVolume,
}) => {
  if (isWork === false) {
    currentTrack = {};
    isPlaying = false;
    volume = 1;
  }
  return (
    <div className="audio-controls">
      {/* Кастомный Controls */}
      <div className="custom-controls">
        <div className="meta-data">
          <img
            id="audioImage2"
            src=
             
              "../music.jpg"
            
          />
          <div className="title-author">
            <span className="title">
              {(currentTrack && currentTrack.trackName) || ""}
            </span>
            <span className="author">
              {(currentTrack && currentTrack.author) || ""}
            </span>
          </div>
        </div>
        <audio
          id="customAudioPlayer"
          ref={audioRef}
          src={(currentTrack && currentTrack.audioUrl) || ""}
          controls
          autoPlay={isPlaying}
          type="audio/mpeg"
        >
          Your browser does not support the audio element.
        </audio>
        <div className="volume-container">
          <input
            className="volume"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={(e) => setVolume(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
};

export default AudioControls;
