import React, { useState } from 'react';

const UploadForm = () => {
    const [title, setTitle] = useState('');
    const [author, setAuthor] = useState('');
    const [genres, setGenres] = useState([]);
    const [duration, setDuration] = useState('');
    const [audioFile, setAudioFile] = useState(null);
    const [imageFile, setImageFile] = useState(null);

    const handleGenreChange = (e, index) => {
        const newGenres = [...genres];
        newGenres[index] = e.target.value;
        setGenres(newGenres);
    };

    const addGenreInput = () => {
        setGenres([...genres, '']);
    };

    const handleFileChange = (e) => {
        if (e.target.name === 'audioFile') {
            setAudioFile(e.target.files[0]);
        } else if (e.target.name === 'imageFile') {
            setImageFile(e.target.files[0]);
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('title', title);
        formData.append('author', author);
        formData.append('audioFile', audioFile);
        formData.append('imageFile', imageFile);
        formData.append('genres', JSON.stringify(genres));
        formData.append('duration', duration);

        fetch('http://localhost:8080/', {
            method: 'POST',
            body: formData
        })
        .then(response => {
            if (response.ok) {
                console.log('Audio file uploaded successfully');
            }
        })
        .catch(error => {
            console.error('Error uploading audio file');
        });
    };

    function getAudioDuration() {
        const audioFile = document.querySelector('input[name="audioFile"]').files[0];

        const audio = new Audio();
        audio.src = URL.createObjectURL(audioFile);

        audio.addEventListener('loadedmetadata', function() {
            const duration = audio.duration;
            // Преобразование продолжительности в формат ЧЧ:ММ:СС
            document.getElementById('durationInput').value = formatDuration(duration);
        });
    }

    function formatDuration(duration) {
        const hours = Math.floor(duration / 3600);
        const minutes = Math.floor((duration % 3600) / 60);
        const seconds = Math.floor(duration % 60);

        let formattedTime = '';
        if (hours > 0) {
            formattedTime += hours + ':';
        }
        if (hours > 0 && minutes < 10) {
            formattedTime += '0';
        }
        formattedTime += String(minutes).padStart(hours > 0 ? 2 : 1, '0') + ':';
        formattedTime += String(seconds).padStart(2, '0');

        return formattedTime;
    }

    return (
        <div>
            <form onSubmit={handleSubmit} encType="multipart/form-data">
            <input type="text" name="title" placeholder="Enter title" value={title} onChange={(e) => setTitle(e.target.value)} /><br />
            <input type="text" name="author" placeholder="Enter author" value={author} onChange={(e) => setAuthor(e.target.value)} /><br /><br />
            {genres.map((genre, index) => (
                <input key={index} type="text" value={genre} onChange={(e) => handleGenreChange(e, index)} placeholder="Enter genre" />
            ))}
            <button type="button" onClick={addGenreInput}>Add Genre</button><br /><br />
            <div>
                <input type="file" name="audioFile" onChange={handleFileChange. getAudioDuration()} /><br />
                <input type="file" name="imageFile" onChange={handleFileChange} />
            </div>
            <input type="submit" value="Upload" />
        </form>
        </div>
    );
};

export default UploadForm;
