import React, { useState, useEffect } from "react";

const AudioList = () => {
  const [audioFiles, setAudioFiles] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => setAudioFiles(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handlePlayAudio = async (audioId, trackName, imageUrl) => {
    try {
      const response = await fetch(`http://localhost:8080/audio/${audioId}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const blob = await response.blob();
      const audioData = URL.createObjectURL(blob);
      setCurrentTrack({ audioUrl: audioData, trackName, imageUrl });
    } catch (error) {
      console.error("Error fetching audio:", error);
    }
  };

  return (
    <div>
      <h1>Play Audio Files</h1>
      <ul>
        {audioFiles.map((audioFile) => (
          <li key={audioFile.id}>
            <div>
              <div>
                {audioFile.image && (
                  <img
                    src={`data:image/jpeg;base64,${audioFile.image.data}`}
                    alt={audioFile.title}
                    style={{ maxWidth: "50px", maxHeight: "50px" }}
                  />
                )}
              </div>
              <div className="control-wrap">
                <div className="control play">
                  <button
                    onClick={() =>
                      handlePlayAudio(
                        audioFile.id,
                        `${audioFile.author} - ${audioFile.title}`,
                        `data:image/jpeg;base64,${audioFile.image.data}`
                      )
                    }
                  >
                    Play
                  </button>
                  <span>
                    {audioFile.author} - {audioFile.title}
                  </span>
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>

      {/* Кастомный Controls */}
      {currentTrack && (
        <div id="customControls">
          <span id="customTrackName">{currentTrack.trackName}</span>
          <div style={{ flexShrink: 0 }}>
            <img
              id="audioImage2"
              src={currentTrack.imageUrl}
              style={{ maxWidth: "50px", maxHeight: "50px" }}
            />
          </div>
          <audio
            id="customAudioPlayer"
            src={currentTrack.audioUrl}
            controls
            style={{ width: "100%" }}
            type="audio/mpeg"
          >
            Your browser does not support the audio element.
          </audio>
        </div>
      )}
    </div>
  );
};

export default AudioList;
