// AudioList.js
import React, { useEffect } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioList.css";

const AudioList = () => {
  const { setCurrentTrack, setIsPlaying, setCurrentTrackIndex, updateAudioFiles } = useAudioContext();

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => updateAudioFiles(data)) // Обновляем audioFiles через переданную функцию
      .catch((error) => console.error("Error fetching data:", error));
  }, [updateAudioFiles]); // Добавляем зависимость

  // Остальной код AudioList остается без изменений
};

export default AudioList;
