import React, { useState } from "react";

const UploadForm = () => {
  const [title, setTitle] = useState("");
  const [author, setAuthor] = useState("");
  const [genres, setGenres] = useState([]);
  const [duration, setDuration] = useState("");
  const [audioFile, setAudioFile] = useState(null);
  const [imageFile, setImageFile] = useState(null);

  const handleGenreChange = (e, index) => {
    const newGenres = [...genres];
    newGenres[index] = e.target.value;
    // setGenres(newGenres);
  };

  const addGenreInput = () => {
    // setGenres([...genres, '']);
  };

  const handleFileChange = (e) => {
    if (e.target.name === "audioFile") {
      setAudioFile(e.target.files[0]);
      getAudioDuration();
    } else if (e.target.name === "imageFile") {
      setImageFile(e.target.files[0]);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("author", author);
    formData.append("audioFile", audioFile);
    formData.append("imageFile", imageFile);
    formData.append("genres", JSON.stringify(genres));
    formData.append("duration", duration);

    fetch("http://localhost:8080/", {
      method: "POST",
      body: formData,
    })
      .then((response) => {
        if (response.ok) {
          console.log("Audio file uploaded successfully");
        }
      })
      .catch((error) => {
        console.error("Error uploading audio file");
      });
  };

  function getAudioDuration() {
    const audioFile = document.querySelector('input[name="audioFile"]')
      .files[0];

    const audio = new Audio();
    audio.src = URL.createObjectURL(audioFile);

    audio.addEventListener("loadedmetadata", function () {
      const duration = audio.duration;
      // Преобразование продолжительности в формат ЧЧ:ММ:СС
      const durationInput = document.getElementById("durationInput");
      if (durationInput) {
        durationInput.value = formatDuration(duration);
      }
      setDuration(formatDuration(duration));
    });
  }

  function formatDuration(duration) {
    const hours = Math.floor(duration / 3600);
    const minutes = Math.floor((duration % 3600) / 60);
    const seconds = Math.floor(duration % 60);

    let formattedTime = "";
    if (hours > 0) {
      formattedTime += hours + ":";
    }
    if (hours > 0 && minutes < 10) {
      formattedTime += "0";
    }
    formattedTime += String(minutes).padStart(hours > 0 ? 2 : 1, "0") + ":";
    formattedTime += String(seconds).padStart(2, "0");

    return formattedTime;
  }

  const FileUploadButton = () => {
  const inputWrapperStyle = {
    width: '100%',
    position: 'relative',
    margin: '15px 0',
    textAlign: 'center',
  };

  const inputFileStyle = {
    opacity: 0,
    visibility: 'hidden',
    position: 'absolute',
  };

  const inputFileIconWrapperStyle = {
    height: '60px',
    width: '60px',
    marginRight: '15px',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    borderRight: '1px solid #fff',
  };

  const inputFileButtonTextStyle = {
    lineHeight: '1',
    marginTop: '1px',
  };

  const inputFileButtonStyle = {
    width: '100%',
    maxWidth: '290px',
    height: '60px',
    background: '#1bbc9b',
    color: '#fff',
    fontSize: '1.125rem',
    fontWeight: '700',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-start',
    borderRadius: '3px',
    cursor: 'pointer',
    margin: '0 auto',
  };

  return (
    <div
      style={{
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        marginTop: "100px",
      }}
    >
      <form onSubmit={handleSubmit} encType="multipart/form-data">
        <input
          style={{ width: "100%" }}
          type="text"
          name="title"
          placeholder="Enter title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <br />
        <input
          style={{ width: "100%", marginTop: "10px" }}
          type="text"
          name="author"
          placeholder="Enter author"
          value={author}
          onChange={(e) => setAuthor(e.target.value)}
        />
        <br />
        <br />
        {genres.map((genre, index) => (
          <input
            key={index}
            type="text"
            value={genre}
            onChange={(e) => handleGenreChange(e, index)}
            placeholder="Enter genre"
          />
        ))}
        <input
          type="text"
          name="duration"
          id="durationInput"
          value={duration}
          hidden
        ></input>
        <button type="button" onClick={addGenreInput}>
          Add Genre
        </button>
        <br />
        <br />
        <div>
          <label for="audioFile">Загрузите аудиофайл:</label> <br />
          <input
            type="file"
            name="audioFile"
            id="uploadAudio"
            onChange={handleFileChange}
          />
          <div class="input__wrapper">
            <input
              name="file"
              type="file"
              id="input__file"
              class="input input__file"
              multiple
            ></input>
            <label for="input__file" class="input__file-button">
              <span class="input__file-icon-wrapper">
                <img
                  class="input__file-icon"
                  src="./img/add.svg"
                  alt="Выбрать файл"
                  width="25"
                ></img>
              </span>
              <span class="input__file-button-text">Выберите файл</span>
            </label>
          </div>
          <br /> <br />
          <label for="imageFile">Загрузите изображение:</label> <br />
          <input
            type="file"
            name="imageFile"
            id="uploadImage"
            onChange={handleFileChange}
          />
        </div>{" "}
        <br />
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <input type="submit" value="Upload" />
        </div>
      </form>
    </div>
  );
}};

export default UploadForm;
