import React, { useState, useEffect, useRef } from "react";
import "./AudioList.css";
import AudioControls from "../audioControls/AudioControls";

const AudioList = ({
  toggleAudioControls,
  setCurrentTrack,
  setIsPlaying,
  setNextTrack,
  audioRef,
  setVolume,
}) => {
const [audioFiles, setAudioFiles] = useState([]);
  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => setAudioFiles(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  // {
  //   /* Кривое кэширование */
  // }
  // // useEffect(() => {
  // //   const cachedAudioFiles = localStorage.getItem("cachedAudioFiles");

  // //   if (cachedAudioFiles) {
  // //     setAudioFiles(JSON.parse(cachedAudioFiles));
  // //   } else {
  // //     fetch("http://localhost:8080/audio", { method: "GET" })
  // //       .then((response) => response.json())
  // //       .then((data) => {
  // //         setAudioFiles(data);
  // //         localStorage.setItem("cachedAudioFiles", JSON.stringify(data));
  // //       })
  // //       .catch((error) => console.error("Error fetching data:", error));
  // //   }
  // // }, []);

  const handlePlayAudio = async (audioId, trackName, author, imageUrl) => {
    if (currentTrack && currentTrack.id === audioId) {
      setIsPlaying(!isPlaying);
      if (isPlaying) {
        audioRef.current.pause();
      } else {
        audioRef.current.play();
      }
    } else {
      try {
        const response = await fetch(`http://localhost:8080/audio/${audioId}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const blob = await response.blob();
        const audioData = URL.createObjectURL(new Blob([blob]));
        handleHideAudioControls();

        // Пробуем предварительно загрузить следующий трек
        const nextIndex =
          audioFiles.findIndex((file) => file.id === audioId) + 1;
        if (nextIndex < audioFiles.length) {
          const nextResponse = await fetch(
            `http://localhost:8080/audio/${audioFiles[nextIndex].id}`
          );
          if (!nextResponse.ok) {
            throw new Error(`HTTP error! status: ${nextResponse.status}`);
          }
          const nextBlob = await nextResponse.blob();
          const nextAudioData = URL.createObjectURL(new Blob([nextBlob]));

          // Предварительно сохраняем следующий трек для использования при окончании текущего
          setNextTrack({
            id: audioFiles[nextIndex].id,
            audioUrl: nextAudioData,
            trackName: audioFiles[nextIndex].title,
            author: audioFiles[nextIndex].author,
            imageUrl: `data:image/jpeg;base64,${audioFiles[nextIndex].image.data}`,
          });
        }

        setCurrentTrack({
          id: audioId,
          audioUrl: audioData,
          trackName,
          author,
          imageUrl,
        });
        setIsPlaying(true);
        setIsWork(false);
      } catch (error) {
        console.error("Error fetching audio:", error);
      }
    }
  };

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = volume;
      audioRef.current.addEventListener("ended", () => {
        if (nextTrack) {
          setCurrentTrack(nextTrack);
          setNextTrack(null); // Сбрасываем следующий трек после использования
        }
      });
    }
  }, [audioFiles, currentTrack, handlePlayAudio, volume, nextTrack]);

  const handleHideAudioControls = () => {
    toggleAudioControls();
  };

  return (
    <div className="audio-list">
      <ul>
        {audioFiles.map((audioFile) => (
          <li>
            <div className="audio-metadata-container">
              {audioFile.image && (
                <img
                  src={`data:image/jpeg;base64,${audioFile.image.data}`}
                  alt={audioFile.title}
                />
              )}
              <div className="button-container">
                <button
                  className="play_pause"
                  onClick={() =>
                    handlePlayAudio(
                      audioFile.id,
                      audioFile.title,
                      audioFile.author,
                      `data:image/jpeg;base64,${audioFile.image.data}`
                    )
                  }
                >
                  {currentTrack && currentTrack.id === audioFile.id && isPlaying
                    ? "||"
                    : ">"}
                </button>
              </div>
              <div className="title-author-container">
                <span className="title">{audioFile.title}</span>
                <span>{audioFile.author}</span>
              </div>
              <div className="duration-conteiner">
                <span className="duration">{audioFile.duration}</span>
              </div>
            </div>
          </li>
        ))}
      </ul>

      {/* Кастомный Controls */}
      <AudioControls
        currentTrack={currentTrack}
        isPlaying={isPlaying}
        volume={volume}
        audioRef={audioRef}
        setVolume={setVolume}
      />

      {/* {currentTrack && (
        <div
          id="customControls"
          style={{
            backgroundColor: "#f0f0f0",
            padding: "10px",
            borderRadius: "4px",
            position: "fixed",
            bottom: 0,
            left: 0,
            right: 0,
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            boxSizing: "border-box",
            width: "100%",
            margin: "0 auto",
          }}
        >
          <div style={{ display: "flex", alignItems: "center" }}>
            <img
              id="audioImage2"
              src={currentTrack.imageUrl}
              style={{
                width: "70px",
                height: "70px",
                marginRight: "10px",
              }}
            />
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                marginLeft: "10px",
                width: "200px",
              }}
            >
              <span
                id="customTrackName"
                style={{
                  fontSize: "20px",
                  fontWeight: "bold",
                  marginBottom: "5px",
                }}
              >
                {currentTrack.trackName}
              </span>
              <span
                id="customAuthor"
                style={{ fontSize: "18px", color: "#6c757d" }}
              >
                {currentTrack.author}
              </span>
            </div>
          </div>
          <audio
            id="customAudioPlayer"
            ref={audioRef}
            src={currentTrack.audioUrl}
            controls
            autoPlay={isPlaying}
            style={{ width: "1200px", marginRight: "130px" }}
            type="audio/mpeg"
          >
            Your browser does not support the audio element.
          </audio>
          <div style={{ display: "flex", alignItems: "center" }}>
            <input
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={(e) => setVolume(e.target.value)}
              style={{
                width: "100px",
                marginRight: "100px",
              }}
            />
          </div>
        </div>
      )} */}
    </div>
  );
};

export default AudioList;
