import React, { useEffect, useRef } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioControls.css";

const AudioControls = () => {
  const { currentTrack, isPlaying, volume, audioRef, setVolume, playNextTrack, playPreviousTrack, currentTrackIndex, audioFiles } = useAudioContext();
  const prevAudioUrl = useRef(null);

    // useEffect(() => {
    //   if (currentTrack && currentTrack.audioUrl !== prevAudioUrl.current) {
    //     audioRef.current.src = currentTrack.audioUrl;
    //     prevAudioUrl.current = currentTrack.audioUrl;
    //   }
    // }, [currentTrack]);

    useEffect(() => {
      if (currentTrack) {
        audioRef.current.src = currentTrack.audioUrl;
      }
    }, [currentTrack]);

  return (
    <div className="audio-controls">
      <div className="custom-controls">
        <div className="meta-data">
          {currentTrack ? (
            <>
              <img id="audioImage2" src={currentTrack.imageUrl} alt={currentTrack.trackName} />
              <div className="title-author">
                <span className="title">{currentTrack.trackName}</span>
                <span className="author">{currentTrack.author}</span>
              </div>
            </>
          ) : (
            <>
              <img id="audioImage2" src="" alt="Track" />
              <div className="title-author">
                <span className="title">Track Name</span>
                <span className="author">Author</span>
              </div>
            </>
          )}
        </div>
        <audio
          id="customAudioPlayer"
          ref={audioRef}
          controls
          autoPlay={isPlaying}
          type="audio/mpeg"
        >
          Your browser does not support the audio element.
        </audio>
        <div className="controls-container">
          <button onClick={playPreviousTrack} disabled={!audioFiles.length || currentTrackIndex === 0}>Previous</button>
          <button onClick={playNextTrack} disabled={!audioFiles.length || currentTrackIndex === audioFiles.length - 1}>Next</button>
        </div>
        <div className="volume-container">
          <input
            className="volume"
            type="range"
            min="0"
            max="1"
            step="0.01"
            value={volume}
            onChange={(e) => setVolume(e.target.value)}
          />
        </div>
      </div>
    </div>
  );
};

export default AudioControls;
