// import "./App.css";
import React, { useState, useRef } from "react";
import AudioList from "./components/audioList/AudioList";
import UploadForm from "./components/uploadAudio/UploadForm";
import Navbar from "./components/Navbar";
import AudioControls from "./components/audioControls/AudioControls";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

function App() {
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);
  const audioRef = useRef(null);

  const handlePlayAudio = (track) => {
    setCurrentTrack(track);
    setIsPlaying(true);
    // Здесь вы можете установить другие состояния, если это необходимо
  };

  return (
    <div className="App">
      <Router>
        <Navbar />
        <Routes>
          <Route path="/audio/upload" element={<UploadForm />} />
          <Route path="/audio" element={<AudioList handlePlayAudio={handlePlayAudio} />} />
        </Routes>
        <AudioControls
          currentTrack={currentTrack}
          isPlaying={isPlaying}
          volume={volume}
          audioRef={audioRef}
          setVolume={setVolume}
        />
      </Router>
    </div>
  );
}

export default App;
