import React, { useEffect } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioList.css";

const AudioList = () => {
  const { currentTrack, isPlaying, togglePlay, audioRef, setCurrentTrackIndex, audioFiles } = useAudioContext();

  useEffect(() => {
    if (currentTrack) {
      audioRef.current.src = currentTrack.audioUrl;
    }
  }, [currentTrack]);

  const handleTogglePlay = () => {
    if (isPlaying) {
      const currentTime = audioRef.current.currentTime;
      audioRef.current.pause();
      audioRef.current.currentTime = currentTime;
    } else {
      audioRef.current.play();
    }
    togglePlay();
  };

  const handlePlayAudio = (index) => {
    setCurrentTrackIndex(index);
  };

  return (
    <div className="audio-list">
      <ul>
        {audioFiles.map((audioFile, index) => (
          <li key={audioFile.id}>
            <div className="audio-metadata-container">
              {/* Render audio metadata */}
              <div className="button-container">
                <button className="play_pause" onClick={() => handlePlayAudio(index)}>
                  {currentTrack && currentTrack.id === audioFile.id ? (isPlaying ? "Pause" : "Play") : "Play"}
                </button>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AudioList;
