import React, { useState, useEffect, useRef } from "react";
import "./AudioList.css";
import AudioControls from "../audioControls/AudioControls";

const AudioList = () => {
  const [audioFiles, setAudioFiles] = useState([]);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [nextTrack, setNextTrack] = useState(false);
  const [hideAudioControls, setHideAudioControls] = useState(false);
  const audioRef = useRef(null);
  const [volume, setVolume] = useState(1);

  const toggleAudioControls = () => {
    setHideAudioControls(!hideAudioControls);
  };

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => setAudioFiles(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, [currentTrack, isPlaying, nextTrack]);


  const handlePlayAudio = async (audioId, trackName, author, imageUrl) => {
    if (currentTrack && currentTrack.id === audioId) {
      setIsPlaying(!isPlaying);
      if (audioRef.current) {
        if (isPlaying) {
          audioRef.current.pause();
        } else {
          audioRef.current.play();
        }
      }
    } else {
      try {
        const response = await fetch(`http://localhost:8080/audio/${audioId}`);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const blob = await response.blob();
        const audioData = URL.createObjectURL(new Blob([blob]));

        setCurrentTrack({
          id: audioId,
          audioUrl: audioData,
          trackName,
          author,
          imageUrl,
        });
        setIsPlaying(true);
      } catch (error) {
        console.error("Error fetching audio:", error);
      }
    }
  };

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.volume = volume;
      audioRef.current.addEventListener("ended", () => {
        if (nextTrack) {
          setCurrentTrack(nextTrack);
          setNextTrack(null);
        }
      });
    }
  }, [volume, nextTrack]);

  return (
    <div className="audio-list">
      <ul>
        {audioFiles.map((audioFile) => (
          <li key={audioFile.id}>
            <div className="audio-metadata-container">
              {audioFile.image && (
                <img
                  src={`data:image/jpeg;base64,${audioFile.image.data}`}
                  alt={audioFile.title}
                />
              )}
              <div className="button-container">
                <button
                  className="play_pause"
                  onClick={() =>
                    handlePlayAudio(
                      audioFile.id,
                      audioFile.title,
                      audioFile.author,
                      `data:image/jpeg;base64,${audioFile.image.data}`
                    )
                  }
                >
                  {currentTrack && currentTrack.id === audioFile.id && isPlaying
                    ? "||"
                    : ">"}
                </button>
              </div>
              <div className="title-author-container">
                <span className="title">{audioFile.title}</span>
                <span>{audioFile.author}</span>
              </div>
              <div className="duration-conteiner">
                <span className="duration">{audioFile.duration}</span>
              </div>
            </div>
          </li>
        ))}
      </ul>

      {/* Кастомный Controls */}
      {currentTrack && (
        <AudioControls
          currentTrack={currentTrack}
          isPlaying={isPlaying}
          volume={volume}
          audioRef={audioRef}
          setVolume={setVolume}
          nextTrack={nextTrack}
          toggleAudioControls={toggleAudioControls} // Передаем функцию toggleAudioControls
        />  
      )}
    </div>
  );
};

export default AudioList;
