import React, { useEffect, useState } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioList.css";

const AudioList = () => {
  const {
    setCurrentTrack,
    isPlaying,
    togglePlay,
    setIsPlaying,
    currentTrackIndex,
    setCurrentTrackIndex,
    updateAudioFiles,
    audioFiles,
    audioRef,
    currentTime,
    setCurrentTime,
  } = useAudioContext();

  const [isMusicPlayingFromControls, setIsMusicPlayingFromControls] = useState(false);

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => {
        if (!isMusicPlayingFromControls) {
          // Обновляем список аудиофайлов только если музыка не играет из AudioControls
          updateAudioFiles(data);
        }
      })
      .catch((error) => console.error("Error fetching data:", error));
  }, [isMusicPlayingFromControls]); // Зависимость от isMusicPlayingFromControls

  const handleTogglePlay = () => {
    if (isPlaying) {
      setCurrentTime(audioRef.current.currentTime);
      audioRef.current.pause();
    } else {
      audioRef.current.currentTime = currentTime;
      audioRef.current.play();
    }
    togglePlay();
  };

  const handlePlayAudio = async (audioFile, index) => {
    console.log(index + " : " + currentTrackIndex)
    if (currentTrackIndex === index) {
      handleTogglePlay(); 
    } else {
      try {
        const response = await fetch(
          `http://localhost:8080/audio/${audioFile.id}`
        );
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const blob = await response.blob();
        const audioData = URL.createObjectURL(new Blob([blob]));

        setCurrentTrackIndex(index);
        setCurrentTrack({
          id: audioFile.id,
          audioUrl: audioData,
          trackName: audioFile.title,
          author: audioFile.author,
          imageUrl: audioFile.image
            ? `data:image/jpeg;base64,${audioFile.image.data}`
            : "",
        });
        setIsPlaying(true);
        setIsMusicPlayingFromControls(false); // Устанавливаем, что музыка не играет из AudioControls
      } catch (error) {
        console.error("Error fetching audio:", error);
      }
    }
  };

  return (
    <div className="audio-list">
      <ul>
        {audioFiles.map((audioFile, index) => (
          <li key={audioFile.id}>
            <div className="audio-metadata-container">
              {audioFile.image && (
                <img
                  src={`data:image/jpeg;base64,${audioFile.image.data}`}
                  alt={audioFile.title}
                />
              )}
              <div className="button-container">
                <button
                  className="play_pause"
                  onClick={() => handlePlayAudio(audioFile, index)}
                >
                  {currentTrackIndex === index && isPlaying ? "||" : ">"}
                </button>
              </div>
              <div className="title-author-container">
                <span className="title">{audioFile.title}</span>
                <span>{audioFile.author}</span>
              </div>
              <div className="duration-container">
                <span className="duration">{audioFile.duration}</span>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AudioList;




