import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import AudioList from "./components/audioList/AudioList";
import UploadForm from "./components/uploadAudio/UploadForm";
import Navbar from "./components/Navbar";
import { AudioProvider } from "./components/AudioContext";
import AudioControls from "./components/audioControls/AudioControls";

function App() {
  const showMainControls = false; // Флаг для отображения основных контролов

  return (
    <div className="App">
      <Router>
        <Navbar />
        <AudioProvider>
          {showMainControls ? ( // 3. Условный рендеринг основных контролов или кастомных
            <AudioControls />
          ) : (
            <Routes>
              <Route path="/audio/upload" element={<UploadForm />} />
              <Route path="/audio" element={<AudioList />} />
            </Routes>
          )}
        </AudioProvider>
      </Router>
    </div>
  );
}

export default App;
