import React, { useState, useEffect, useRef } from "react";
import "./AudioList.css";
import AudioControls from "../audioControls/AudioControls";
import { useAudioContext } from "../AudioContext";

const AudioList = () => {
  const {
    audioFiles,
    setCurrentTrack,
    setIsPlaying,
    setNextTrack,
    setVolume,
  } = useAudioContext();
  const [currentTrackIndex, setCurrentTrackIndex] = useState(null);
  const audioRef = useRef(null);

  const handlePlayAudio = (audioId, index) => {
    setCurrentTrack(audioFiles[index]);
    setIsPlaying(true);
    setCurrentTrackIndex(index);
  };

  useEffect(() => {
    if (audioRef.current) {
      audioRef.current.addEventListener("ended", () => {
        setNextTrack(null);
        setCurrentTrackIndex(null);
      });
    }
  }, [audioRef, setNextTrack, setCurrentTrackIndex]);

  return (
    <div className="audio-list">
      <ul>
        {audioFiles.map((audioFile, index) => (
          <li key={audioFile.id}>
            <div className="audio-metadata-container">
              {audioFile.image && (
                <img
                  src={`data:image/jpeg;base64,${audioFile.image.data}`}
                  alt={audioFile.title}
                />
              )}
              <div className="button-container">
                <button
                  className="play_pause"
                  onClick={() => handlePlayAudio(audioFile.id, index)}
                >
                  {currentTrackIndex === index ? "||" : ">"}
                </button>
              </div>
              <div className="title-author-container">
                <span className="title">{audioFile.title}</span>
                <span>{audioFile.author}</span>
              </div>
              <div className="duration-conteiner">
                <span className="duration">{audioFile.duration}</span>
              </div>
            </div>
          </li>
        ))}
      </ul>

      {currentTrackIndex !== null && (
        <AudioControls audioRef={audioRef} />
      )}
    </div>
  );
};

export default AudioList;
