import React, { useState, useEffect } from 'react';

const AudioList = () => {
  const [audioFiles, setAudioFiles] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8080/') // Здесь должен быть URL для получения списка аудиофайлов с сервера
      .then(response => response.json())
      .then(data => setAudioFiles(data));
  }, []);

  return (
    <div id="audioList">
      <h1>Play Audio Files</h1>
      <ul>
        {audioFiles.map(audioFile => (
          <li key={audioFile.id}>
            <div>
              <div>
                <img id="audioImage" src={"http://localhost:8080/audio/${audioFile.id}/image"} alt="Audio Image" style={{ maxWidth: '50px', maxHeight: '50px' }} />
              </div>
              <div style={{ marginLeft: '10px' }}>
                <div>
                  <span>{audioFile.author} - {audioFile.title}</span>
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AudioList;
