import React, { createContext, useState, useContext, useEffect, useRef } from "react";
import PropTypes from "prop-types";

const AudioContext = createContext();

export const useAudioContext = () => useContext(AudioContext);

export const AudioProvider = ({ children }) => {
  const [currentTrack, setCurrentTrack] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [volume, setVolume] = useState(1);
  const [audioFiles, setAudioFiles] = useState([]);
  const [currentTrackId, setCurrentTrackId] = useState(null); // Добавляем состояние для отслеживания текущего ID трека
  const audioRef = useRef(null);

  const updateAudioFiles = (data) => {
    setAudioFiles(data);
  };

  useEffect(() => {
    if (currentTrackId !== null) {
      const track = audioFiles.find((audio) => audio.id === currentTrackId);
      setCurrentTrack(track);
    }
  }, [currentTrackId, audioFiles]);

  return (
    <AudioContext.Provider
      value={{
        currentTrack,
        setCurrentTrack,
        isPlaying,
        setIsPlaying,
        volume,
        setVolume,
        audioFiles,
        updateAudioFiles,
        currentTrackId,
        setCurrentTrackId,
        audioRef,
      }}
    >
      {children}
    </AudioContext.Provider>
  );
};

AudioProvider.propTypes = {
  children: PropTypes.node.isRequired,
};
