// AudioList.js
import React, { useEffect } from "react";
import { useAudioContext } from "../AudioContext";
import "./AudioList.css";

const AudioList = () => {
  const { setCurrentTrack, setIsPlaying, setCurrentTrackIndex, updateAudioFiles, audioFiles, currentTrackIndex, isPlaying } = useAudioContext();

  useEffect(() => {
    fetch("http://localhost:8080/audio", { method: "GET" })
      .then((response) => response.json())
      .then((data) => updateAudioFiles(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handlePlayAudio = async (audioFile, index) => {
    try {
      const response = await fetch(`http://localhost:8080/audio/${audioFile.id}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const blob = await response.blob();
      const audioData = URL.createObjectURL(new Blob([blob]));

      setCurrentTrackIndex(index);
      setCurrentTrack({
        id: audioFile.id,
        audioUrl: audioData,
        trackName: audioFile.title,
        author: audioFile.author,
        imageUrl: audioFile.image ? `data:image/jpeg;base64,${audioFile.image.data}` : "",
      });

      // Если текущий трек совпадает с треком, который уже играет, то переключаем состояние воспроизведения,
      // иначе начинаем воспроизведение нового трека.
      if (currentTrackIndex === index) {
        setIsPlaying((prevIsPlaying) => !prevIsPlaying);
      } else {
        setIsPlaying(true);
      }
    } catch (error) {
      console.error("Error fetching audio:", error);
    }
  };

  return (
    <div className="audio-list">
      <ul>
        {audioFiles.map((audioFile, index) => (
          <li key={audioFile.id}>
            <div className="audio-metadata-container">
              {audioFile.image && (
                <img
                  src={`data:image/jpeg;base64,${audioFile.image.data}`}
                  alt={audioFile.title}
                />
              )}
              <div className="button-container">
                <button
                  className="play_pause"
                  onClick={() => handlePlayAudio(audioFile, index)}
                >
                  {/* Проверяем, является ли текущий трек выбранным и играет ли он */}
                  {currentTrackIndex === index && isPlaying ? "Pause" : "Play"}
                </button>
              </div>
              <div className="title-author-container">
                <span className="title">{audioFile.title}</span>
                <span>{audioFile.author}</span>
              </div>
              <div className="duration-container">
                <span className="duration">{audioFile.duration}</span>
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AudioList;
